"use client";

import { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import {
    MessageSquare,
    Flame,
    Code2,
    Bug,
    Send,
    Sparkles,
    ChevronRight,
    Zap,
    Brain
} from "lucide-react";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";

const TOOLS = [
    {
        id: "qa",
        name: "Portfolio Q&A",
        description: "Ask anything about Adnan's skills and projects.",
        icon: MessageSquare,
        color: "text-blue-500",
        bgColor: "bg-blue-500/10",
        prompt: "Ask me about Adnan's Next.js experience or his latest projects..."
    },
    {
        id: "roaster",
        name: "Project Roaster",
        description: "Get a witty senior dev critique of your idea.",
        icon: Flame,
        color: "text-orange-500",
        bgColor: "bg-orange-500/10",
        prompt: "Paste your project idea or code here for a roast..."
    },
    {
        id: "generator",
        name: "Component Generator",
        description: "Describe a UI component, get React code.",
        icon: Code2,
        color: "text-purple-500",
        bgColor: "bg-purple-500/10",
        prompt: "Describe the component (e.g. 'A glassy landing page hero')..."
    },
    {
        id: "fixer",
        name: "Error Fixer",
        description: "Paste your error log and get an instant fix.",
        icon: Bug,
        color: "text-red-500",
        bgColor: "bg-red-500/10",
        prompt: "Paste your error message or stack trace..."
    }
];

export default function AILabPage() {
    const [activeTool, setActiveTool] = useState(TOOLS[0]);
    const [model, setModel] = useState<"groq" | "deepseek">("groq");
    const [input, setInput] = useState("");
    const [history, setHistory] = useState<Record<string, { user: string; ai: string }>>({});
    const [isLoading, setIsLoading] = useState(false);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        const currentInput = input;
        if (!currentInput.trim() || isLoading) return;

        // Clear input immediately for better UX
        setInput("");
        setIsLoading(true);

        // Capture the id of the tool that initiated the request
        const currentToolId = activeTool.id;

        // Initialize history with user message and empty AI response
        setHistory(prev => ({
            ...prev,
            [currentToolId]: { user: currentInput, ai: "..." }
        }));

        try {
            const res = await fetch("/api/ai-lab", {
                method: "POST",
                body: JSON.stringify({
                    message: currentInput,
                    type: currentToolId,
                    model
                }),
            });

            if (!res.ok) throw new Error("Failed to fetch");

            const reader = res.body?.getReader();
            const decoder = new TextDecoder();

            if (!reader) throw new Error("No reader available");

            let accumulated = "";

            while (true) {
                const { done, value } = await reader.read();
                if (done) break;

                accumulated += decoder.decode(value, { stream: true });

                setHistory(prev => ({
                    ...prev,
                    [currentToolId]: { user: currentInput, ai: accumulated }
                }));
            }
        } catch (error) {
            console.error("AI Lab Error:", error);
            setHistory(prev => ({
                ...prev,
                [currentToolId]: { user: currentInput, ai: "Oops! Something went wrong. Please check your connection and try again." }
            }));
        } finally {
            setIsLoading(false);
        }
    };

    const currentChat = history[activeTool.id];

    return (
        <main className="min-h-screen pt-24 pb-12 bg-slate-950 text-white selection:bg-primary/30">
            <div className="container mx-auto px-6 max-w-6xl">
                {/* Header Section */}
                <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-6 mb-12">
                    <div>
                        <motion.h1
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            className="text-4xl md:text-5xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-primary via-purple-500 to-blue-500 mb-2"
                        >
                            AI Lab
                        </motion.h1>
                        <p className="text-slate-400 text-lg">Interactive experiments powered by high-speed neural networks.</p>
                    </div>

                    {/* Model Switcher */}
                    <div className="flex p-1 bg-slate-900 rounded-xl border border-slate-800 shadow-2xl">
                        <button
                            onClick={() => setModel("groq")}
                            className={cn(
                                "flex items-center gap-2 px-4 py-2 rounded-lg text-sm font-bold transition-all duration-300",
                                model === "groq" ? "bg-primary text-white shadow-lg" : "text-slate-500 hover:text-slate-300"
                            )}
                        >
                            <Zap className="w-4 h-4" /> Groq (Fast)
                        </button>
                        <button
                            onClick={() => setModel("deepseek")}
                            className={cn(
                                "flex items-center gap-2 px-4 py-2 rounded-lg text-sm font-bold transition-all duration-300",
                                model === "deepseek" ? "bg-primary text-white shadow-lg" : "text-slate-500 hover:text-slate-300"
                            )}
                        >
                            <Brain className="w-4 h-4" /> DeepSeek (Logic)
                        </button>
                    </div>
                </div>

                <div className="grid grid-cols-1 lg:grid-cols-12 gap-8 items-start">
                    {/* Sidebar / Tool Selection */}
                    <div className="lg:col-span-4 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-1 gap-4">
                        {TOOLS.map((tool) => (
                            <button
                                key={tool.id}
                                onClick={() => setActiveTool(tool)}
                                className={cn(
                                    "relative group flex flex-col p-5 rounded-2xl border transition-all duration-500 text-left overflow-hidden",
                                    activeTool.id === tool.id
                                        ? "bg-slate-900 border-primary/50 ring-1 ring-primary/20"
                                        : "bg-slate-900/40 border-slate-800 hover:border-slate-700"
                                )}
                            >
                                <div className={cn("p-2 w-fit rounded-lg mb-4 transition-transform group-hover:scale-110", tool.bgColor)}>
                                    <tool.icon className={cn("w-6 h-6", tool.color)} />
                                </div>
                                <h3 className="text-lg font-bold mb-1">{tool.name}</h3>
                                <p className="text-sm text-slate-500 leading-relaxed">{tool.description}</p>

                                {activeTool.id === tool.id && (
                                    <motion.div
                                        layoutId="toolBadge"
                                        className="absolute top-4 right-4"
                                    >
                                        <Sparkles className="w-4 h-4 text-primary animate-pulse" />
                                    </motion.div>
                                )}
                            </button>
                        ))}
                    </div>

                    {/* Playground Interface */}
                    <div className="lg:col-span-8 flex flex-col gap-6">
                        <div className="bg-slate-900/60 backdrop-blur-xl border border-slate-800 rounded-3xl p-6 md:p-8 shadow-2xl relative overflow-hidden flex flex-col h-full min-h-[500px]">

                            {/* Interaction UI */}
                            <div className="flex-1 flex flex-col gap-6">
                                <div className="flex items-center gap-3 pb-6 border-b border-slate-800">
                                    <div className={cn("p-2 rounded-lg", activeTool.bgColor)}>
                                        <activeTool.icon className={cn("w-5 h-5", activeTool.color)} />
                                    </div>
                                    <h2 className="text-xl font-bold">{activeTool.name} Playground</h2>
                                </div>

                                {/* Response Log */}
                                <div className="flex-1 overflow-y-auto space-y-6 min-h-[250px] relative scrollbar-hide py-4">
                                    <AnimatePresence mode="wait">
                                        {currentChat ? (
                                            <motion.div
                                                key={activeTool.id + currentChat.user}
                                                initial={{ opacity: 0, y: 10 }}
                                                animate={{ opacity: 1, y: 0 }}
                                                className="space-y-6"
                                            >
                                                {/* User Message */}
                                                <div className="flex justify-end">
                                                    <div className="bg-primary/10 border border-primary/20 px-5 py-3 rounded-2xl max-w-[80%] text-sm text-primary-foreground/90">
                                                        {currentChat.user}
                                                    </div>
                                                </div>
                                                {/* AI Response */}
                                                <div className="bg-slate-800/40 p-6 rounded-2xl border border-slate-700 font-mono text-sm whitespace-pre-wrap leading-relaxed">
                                                    {currentChat.ai}
                                                </div>
                                            </motion.div>
                                        ) : !isLoading && (
                                            <motion.div
                                                initial={{ opacity: 0 }}
                                                animate={{ opacity: 1 }}
                                                className="h-full flex flex-col items-center justify-center text-slate-600 italic py-12"
                                            >
                                                <activeTool.icon className="w-12 h-12 mb-4 opacity-20" />
                                                Enter a prompt to start {activeTool.name}...
                                            </motion.div>
                                        )}
                                    </AnimatePresence>
                                    {isLoading && (
                                        <div className="flex items-center gap-2 text-primary animate-pulse py-2">
                                            <Sparkles className="w-4 h-4" /> Thinking with {model === "groq" ? "Groq" : "DeepSeek"}...
                                        </div>
                                    )}
                                </div>

                                {/* Input Area */}
                                <form onSubmit={handleSubmit} className="relative group mt-auto">
                                    <textarea
                                        value={input}
                                        onChange={(e) => setInput(e.target.value)}
                                        placeholder={activeTool.prompt}
                                        className="w-full bg-slate-800/50 border border-slate-700 rounded-2xl px-5 py-4 pr-16 focus:outline-none focus:ring-2 focus:ring-primary/50 focus:border-primary transition-all resize-none h-32 text-sm md:text-base"
                                        onKeyDown={(e) => {
                                            if (e.key === "Enter" && !e.shiftKey) {
                                                e.preventDefault();
                                                handleSubmit(e);
                                            }
                                        }}
                                    />
                                    <Button
                                        type="submit"
                                        disabled={isLoading || !input}
                                        className="absolute right-3 bottom-3 h-10 w-10 p-0 rounded-xl bg-primary hover:bg-primary/80 text-white shadow-xl shadow-primary/20"
                                    >
                                        <Send className="w-5 h-5" />
                                    </Button>
                                </form>
                            </div>

                            {/* Decorative Mesh */}
                            <div className="absolute top-0 right-0 -z-10 w-64 h-64 bg-primary/5 blur-[100px] rounded-full" />
                            <div className="absolute bottom-0 left-0 -z-10 w-64 h-64 bg-blue-500/5 blur-[100px] rounded-full" />
                        </div>

                        <p className="text-center text-xs text-slate-600 px-4">
                            Generated content may be inaccurate. Experiments are powered by {model === "groq" ? "Llama 3.3 (Groq)" : "DeepSeek-V3"}.
                        </p>
                    </div>
                </div>
            </div>
        </main>
    );
}

'use client';

import { useState, useRef, useEffect } from 'react';
import { Send, Cpu, Sparkles, Lightbulb, ArrowDown, Trash2 } from 'lucide-react';
import ReactMarkdown from 'react-markdown';
import { cn } from '@/lib/utils';
import { motion, AnimatePresence } from 'framer-motion';

interface Message {
    id: string;
    role: 'user' | 'assistant';
    content: string;
}

const examplePrompts = [
    "A real-time chat app for remote teams using WebSockets",
    "An AI-powered e-commerce recommendation engine",
    "A multi-tenant SaaS dashboard with analytics",
    "A social media scheduling tool with AI content generation",
];

export default function ArchitectPage() {
    const [messages, setMessages] = useState<Message[]>([]);
    const [input, setInput] = useState('');
    const [loading, setLoading] = useState(false);
    const messagesEndRef = useRef<HTMLDivElement>(null);
    const textareaRef = useRef<HTMLTextAreaElement>(null);



    // Auto-resize textarea
    useEffect(() => {
        if (textareaRef.current) {
            textareaRef.current.style.height = 'auto';
            textareaRef.current.style.height = Math.min(textareaRef.current.scrollHeight, 160) + 'px';
        }
    }, [input]);

    const handleSubmit = async (promptText?: string) => {
        const text = promptText || input;
        if (!text.trim() || loading) return;

        const userMessage: Message = {
            id: Date.now().toString(),
            role: 'user',
            content: text.trim(),
        };

        const assistantMessage: Message = {
            id: (Date.now() + 1).toString(),
            role: 'assistant',
            content: '',
        };

        setMessages((prev) => [...prev, userMessage, assistantMessage]);
        setInput('');
        setLoading(true);

        try {
            const res = await fetch('/api/architect', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ prompt: text }),
            });

            if (!res.ok) throw new Error(res.statusText);

            const reader = res.body?.getReader();
            const decoder = new TextDecoder();

            if (reader) {
                while (true) {
                    const { done, value } = await reader.read();
                    if (done) break;
                    const chunk = decoder.decode(value);
                    setMessages((prev) => {
                        const updated = [...prev];
                        const lastMsg = updated[updated.length - 1];
                        if (lastMsg.role === 'assistant') {
                            lastMsg.content += chunk;
                        }
                        return updated;
                    });
                }
            }
        } catch (error) {
            console.error('Failed to generate architecture:', error);
            setMessages((prev) => {
                const updated = [...prev];
                const lastMsg = updated[updated.length - 1];
                if (lastMsg.role === 'assistant') {
                    lastMsg.content = '**Error**: Failed to contact the System Architect. Please try again.';
                }
                return updated;
            });
        } finally {
            setLoading(false);
        }
    };

    const clearChat = () => {
        setMessages([]);
        setInput('');
    };

    const handleKeyDown = (e: React.KeyboardEvent) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault();
            handleSubmit();
        }
    };

    return (
        <div className="flex flex-col h-screen max-w-5xl mx-auto pt-24">
            {/* Messages Area */}
            <div className="flex-1 overflow-y-auto px-4 md:px-6 py-6 no-scrollbar">
                {messages.length === 0 ? (
                    <EmptyState onPromptClick={(p) => handleSubmit(p)} />
                ) : (
                    <div className="space-y-6 pb-4">
                        {messages.map((message) => (
                            <MessageBubble key={message.id} message={message} isLoading={loading && message.role === 'assistant' && message === messages[messages.length - 1]} />
                        ))}
                        <div ref={messagesEndRef} />
                    </div>
                )}
            </div>

            {/* Sticky Input Bar */}
            <div className="border-t border-border bg-background/90 backdrop-blur-xl px-4 md:px-6 py-4">
                <div className="flex items-end gap-3 max-w-4xl mx-auto">
                    {messages.length > 0 && (
                        <button
                            onClick={clearChat}
                            className="p-2.5 rounded-xl text-muted-foreground hover:text-destructive hover:bg-destructive/10 transition-all duration-300 shrink-0 mb-0.5"
                            title="Clear chat"
                        >
                            <Trash2 className="h-4 w-4" />
                        </button>
                    )}

                    <div className="flex-1 relative bg-card border border-border rounded-2xl focus-within:border-primary focus-within:ring-2 focus-within:ring-primary/20 transition-all duration-300 overflow-hidden">
                        <textarea
                            ref={textareaRef}
                            value={input}
                            onChange={(e) => setInput(e.target.value)}
                            onKeyDown={handleKeyDown}
                            placeholder="Describe your app idea..."
                            className="w-full px-4 py-3.5 pr-12 bg-transparent resize-none focus:outline-none text-sm min-h-[48px] max-h-[160px]"
                            disabled={loading}
                            rows={1}
                        />
                        <div className="absolute right-2 bottom-2">
                            <span className="text-[10px] text-muted-foreground/50 mr-1">{input.length}</span>
                        </div>
                    </div>

                    <button
                        onClick={() => handleSubmit()}
                        disabled={loading || !input.trim()}
                        className={cn(
                            "p-3 rounded-xl shrink-0 transition-all duration-300 mb-0.5",
                            loading
                                ? "bg-secondary text-muted-foreground cursor-wait"
                                : input.trim()
                                    ? "bg-primary text-primary-foreground hover:bg-[var(--shift-hover)]"
                                    : "bg-secondary text-muted-foreground"
                        )}
                    >
                        {loading ? (
                            <Sparkles className="h-5 w-5 animate-spin" />
                        ) : (
                            <Send className="h-5 w-5" />
                        )}
                    </button>
                </div>

            </div>
        </div>
    );
}

function EmptyState({ onPromptClick }: { onPromptClick: (prompt: string) => void }) {
    return (
        <div className="flex flex-col items-center justify-center h-full text-center px-4">
            <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
                className="max-w-lg"
            >
                <div className="w-16 h-16 rounded-2xl bg-primary/10 text-primary flex items-center justify-center mx-auto mb-6">
                    <Cpu className="h-8 w-8" />
                </div>
                <h1 className="text-3xl md:text-4xl font-extrabold tracking-tight mb-3">
                    System <span className="text-primary">Architect</span>
                </h1>
                <p className="text-muted-foreground mb-10 leading-relaxed">
                    Describe your app idea below. I&apos;ll generate a comprehensive technical proposal, tech stack, and roadmap.
                </p>

                <div className="space-y-3">
                    <div className="flex items-center gap-2 text-sm text-muted-foreground mb-4">
                        <Lightbulb className="h-4 w-4 text-primary" />
                        <span className="font-semibold">Try an example</span>
                    </div>
                    {examplePrompts.map((prompt) => (
                        <button
                            key={prompt}
                            onClick={() => onPromptClick(prompt)}
                            className="w-full text-left p-4 rounded-xl border border-border bg-card hover:border-primary hover:bg-primary/5 transition-all duration-300 text-sm text-muted-foreground hover:text-foreground"
                        >
                            &ldquo;{prompt}&rdquo;
                        </button>
                    ))}
                </div>
            </motion.div>
        </div>
    );
}

function MessageBubble({ message, isLoading }: { message: Message; isLoading: boolean }) {
    const isUser = message.role === 'user';

    return (
        <motion.div
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
            className={cn("flex", isUser ? "justify-end" : "justify-start")}
        >
            <div
                className={cn(
                    "max-w-[85%] md:max-w-[75%] rounded-2xl px-5 py-4",
                    isUser
                        ? "bg-primary text-primary-foreground rounded-br-md"
                        : "bg-card border border-border rounded-bl-md"
                )}
            >
                {isUser ? (
                    <p className="text-sm leading-relaxed whitespace-pre-wrap">{message.content}</p>
                ) : (
                    <>
                        {message.content ? (
                            <div className="prose prose-sm dark:prose-invert prose-headings:font-bold prose-headings:tracking-tight prose-p:text-muted-foreground prose-strong:text-foreground prose-code:text-primary prose-code:bg-primary/10 prose-code:rounded-md prose-code:px-1.5 prose-code:py-0.5 max-w-none">
                                <ReactMarkdown>{message.content}</ReactMarkdown>
                            </div>
                        ) : isLoading ? (
                            <div className="flex items-center gap-1.5 py-1">
                                <span className="w-2 h-2 rounded-full bg-primary typing-dot-1" />
                                <span className="w-2 h-2 rounded-full bg-primary typing-dot-2" />
                                <span className="w-2 h-2 rounded-full bg-primary typing-dot-3" />
                            </div>
                        ) : null}
                    </>
                )}
            </div>
        </motion.div>
    );
}

"use client";

import { useState, useEffect, useMemo } from "react";
import { motion, AnimatePresence } from "framer-motion";
import {
    Sparkles,
    Layout,
    Palette,
    Settings,
    Wand2,
    Rocket,
    Eye,
    Check,
    ChevronRight,
    ArrowLeft,
    ArrowRight,
    Monitor,
    Smartphone,
    Tablet,
    Layers,
    Code2,
    Globe,
    Zap,
    Star,
    Cpu
} from "lucide-react";
import Link from "next/link";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";

// --- Types ---
type Step = "identity" | "style" | "content" | "review";

const THEMES = [
    { id: "dark-glass", name: "Midnight Glass", color: "bg-slate-900", accent: "blue" },
    { id: "brutal", name: "Neo Brutalist", color: "bg-white", accent: "black" },
    { id: "monochrome", name: "Architect Mono", color: "bg-[#050505]", accent: "slate" },
    { id: "vibes", name: "Cyber Sunset", color: "bg-purple-950", accent: "pink" }
];

export default function AIPortfolioBuilder() {
    const [step, setStep] = useState<Step>("identity");
    const [name, setName] = useState("");
    const [role, setRole] = useState("");
    const [selectedTheme, setSelectedTheme] = useState(THEMES[0]);
    const [isGenerating, setIsGenerating] = useState(false);
    const [isComplete, setIsComplete] = useState(false);
    const [mounted, setMounted] = useState(false);

    useEffect(() => {
        const timer = setTimeout(() => {
            setMounted(true);
        }, 0);
        return () => clearTimeout(timer);
    }, []);

    const progress = useMemo(() => {
        if (step === "identity") return 25;
        if (step === "style") return 50;
        if (step === "content") return 75;
        return 100;
    }, [step]);

    const handleGenerate = () => {
        setIsGenerating(true);
        setTimeout(() => {
            setIsGenerating(false);
            setIsComplete(true);
        }, 3000);
    };

    if (!mounted) return null;

    return (
        <div className="min-h-screen bg-[#020205] text-[#E0E0E0] font-sans selection:bg-blue-500/30 overflow-hidden flex flex-col">

            {/* --- Stepper Header --- */}
            <header className="h-20 border-b border-white/5 bg-[#020205]/80 backdrop-blur-xl flex items-center justify-between px-8 z-50">
                <div className="flex items-center gap-4">
                    <Link href="/projects" className="p-2 rounded-xl hover:bg-white/5 transition-colors">
                        <ArrowLeft className="w-5 h-5" />
                    </Link>
                    <div className="flex items-center gap-2">
                        <div className="w-8 h-8 rounded-lg bg-blue-600 flex items-center justify-center">
                            <Cpu className="w-5 h-5 text-white" />
                        </div>
                        <span className="text-lg font-black tracking-tighter uppercase italic">NexusGen.</span>
                    </div>
                </div>

                <div className="hidden md:flex items-center gap-8">
                    {["Identity", "Style", "Content", "Review"].map((s, i) => (
                        <div key={s} className="flex items-center gap-2">
                            <div className={cn(
                                "w-6 h-6 rounded-full flex items-center justify-center text-[10px] font-black border",
                                progress >= (i + 1) * 25 ? "bg-blue-600 border-blue-600 text-white" : "border-white/10 text-slate-600"
                            )}>
                                {progress > (i + 1) * 25 ? <Check className="w-3 h-3" /> : (i + 1)}
                            </div>
                            <span className={cn(
                                "text-[10px] font-black uppercase tracking-widest",
                                step.toLowerCase() === s.toLowerCase() ? "text-white" : "text-slate-600"
                            )}>{s}</span>
                            {i < 3 && <div className="h-[1px] w-6 bg-white/5 mx-2" />}
                        </div>
                    ))}
                </div>

                <div className="flex items-center gap-4">
                    <Button variant="ghost" className="text-xs font-bold text-slate-500 hover:text-white uppercase tracking-widest">
                        Drafts
                    </Button>
                </div>
            </header>

            {/* --- Main Content Area --- */}
            <main className="flex-1 flex overflow-hidden">

                {/* Left: Configuration Panel */}
                <div className="w-full lg:w-[450px] border-r border-white/5 h-full p-10 flex flex-col justify-between overflow-y-auto custom-scrollbar">
                    <div>
                        <AnimatePresence mode="wait">
                            {step === "identity" && (
                                <motion.div initial={{ opacity: 0, x: -20 }} animate={{ opacity: 1, x: 0 }} exit={{ opacity: 0, x: 20 }} className="space-y-8">
                                    <div>
                                        <span className="text-[10px] font-black uppercase tracking-[0.4em] text-blue-500 mb-4 block">Basic Identity</span>
                                        <h2 className="text-3xl font-black text-white tracking-tighter mb-4">Who are you?</h2>
                                        <p className="text-slate-500 text-sm leading-relaxed">Let's start with your legacy details. This will seed the AI generation engine.</p>
                                    </div>
                                    <div className="space-y-6">
                                        <div className="space-y-2">
                                            <label className="text-[10px] font-black uppercase tracking-widest text-[#404040]">Legal Name</label>
                                            <input
                                                type="text"
                                                placeholder="e.g. Adnan Khan"
                                                className="w-full h-14 bg-white/5 border border-white/10 rounded-2xl px-6 text-sm font-bold focus:border-blue-500/50 transition-all outline-none"
                                                value={name}
                                                onChange={(e) => setName(e.target.value)}
                                            />
                                        </div>
                                        <div className="space-y-2">
                                            <label className="text-[10px] font-black uppercase tracking-widest text-[#404040]">Craft / Role</label>
                                            <input
                                                type="text"
                                                placeholder="e.g. Senior OS Architect"
                                                className="w-full h-14 bg-white/5 border border-white/10 rounded-2xl px-6 text-sm font-bold focus:border-blue-500/50 transition-all outline-none"
                                                value={role}
                                                onChange={(e) => setRole(e.target.value)}
                                            />
                                        </div>
                                    </div>
                                </motion.div>
                            )}

                            {step === "style" && (
                                <motion.div initial={{ opacity: 0, x: -20 }} animate={{ opacity: 1, x: 0 }} exit={{ opacity: 0, x: 20 }} className="space-y-8">
                                    <div>
                                        <span className="text-[10px] font-black uppercase tracking-[0.4em] text-purple-500 mb-4 block">Visual Language</span>
                                        <h2 className="text-3xl font-black text-white tracking-tighter mb-4">Aesthetic Mood.</h2>
                                        <p className="text-slate-500 text-sm leading-relaxed">Choose the design tokens that best represent your digital presence.</p>
                                    </div>
                                    <div className="grid grid-cols-2 gap-4">
                                        {THEMES.map(theme => (
                                            <button
                                                key={theme.id}
                                                onClick={() => setSelectedTheme(theme)}
                                                className={cn(
                                                    "relative p-6 rounded-3xl border transition-all text-left group",
                                                    selectedTheme.id === theme.id ? "bg-white/5 border-blue-500/50" : "bg-white/2 border-white/5 hover:border-white/20"
                                                )}
                                            >
                                                <div className={cn("w-10 h-10 rounded-xl mb-4 shadow-xl", theme.color)} />
                                                <h4 className="text-sm font-bold text-white group-hover:text-blue-500 transition-colors">{theme.name}</h4>
                                                {selectedTheme.id === theme.id && <div className="absolute top-4 right-4 w-5 h-5 rounded-full bg-blue-600 flex items-center justify-center text-white"><Check className="w-3 h-3" /></div>}
                                            </button>
                                        ))}
                                    </div>
                                </motion.div>
                            )}

                            {step === "content" && (
                                <motion.div initial={{ opacity: 0, x: -20 }} animate={{ opacity: 1, x: 0 }} exit={{ opacity: 0, x: 20 }} className="space-y-8">
                                    <div>
                                        <span className="text-[10px] font-black uppercase tracking-[0.4em] text-emerald-500 mb-4 block">Component Stack</span>
                                        <h2 className="text-3xl font-black text-white tracking-tighter mb-4">Core Sections.</h2>
                                        <p className="text-slate-500 text-sm leading-relaxed">Select the architectural blocks you want to include in your structure.</p>
                                    </div>
                                    <div className="space-y-4">
                                        {[
                                            { id: "hero", name: "High-Fidelity Hero", icon: Sparkles },
                                            { id: "projects", name: "Project Showcases", icon: Layout },
                                            { id: "skills", name: "Skill Matrices", icon: Zap },
                                            { id: "contact", name: "Connect Portal", icon: Globe }
                                        ].map(section => (
                                            <div key={section.id} className="flex items-center justify-between p-5 rounded-[2rem] bg-white/2 border border-white/5">
                                                <div className="flex items-center gap-4">
                                                    <div className="w-10 h-10 rounded-xl bg-white/5 flex items-center justify-center text-slate-500"><section.icon className="w-5 h-5" /></div>
                                                    <span className="text-sm font-bold text-white">{section.name}</span>
                                                </div>
                                                <div className="w-12 h-6 bg-blue-600 rounded-full flex items-center px-1"><div className="w-4 h-4 bg-white rounded-full translate-x-6" /></div>
                                            </div>
                                        ))}
                                    </div>
                                </motion.div>
                            )}

                            {step === "review" && (
                                <motion.div initial={{ opacity: 0, x: -20 }} animate={{ opacity: 1, x: 0 }} exit={{ opacity: 0, x: 20 }} className="space-y-8">
                                    <div>
                                        <span className="text-[10px] font-black uppercase tracking-[0.4em] text-amber-500 mb-4 block">Final Sync</span>
                                        <h2 className="text-3xl font-black text-white tracking-tighter mb-4">Ready for Launch.</h2>
                                        <p className="text-slate-500 text-sm leading-relaxed">The AI has prepared your blueprint. Initiate deployment to finalize your portfolio.</p>
                                    </div>
                                    <div className="p-8 rounded-[2rem] bg-slate-900/40 border border-white/5">
                                        <div className="flex items-center gap-4 mb-8 pb-8 border-b border-white/5">
                                            <div className="w-12 h-12 rounded-2xl bg-gradient-to-tr from-blue-600 to-indigo-600 flex items-center justify-center font-black text-sm">AK</div>
                                            <div>
                                                <h4 className="font-bold text-white uppercase">{name || "Adnan Khan"}</h4>
                                                <p className="text-xs text-slate-500 font-medium">{role || "System Architect"}</p>
                                            </div>
                                        </div>
                                        <div className="space-y-4 text-xs font-bold text-slate-400">
                                            <div className="flex justify-between"><span>Active Theme</span><span className="text-white">{selectedTheme.name}</span></div>
                                            <div className="flex justify-between"><span>Sections</span><span className="text-white">4 Applied</span></div>
                                        </div>
                                    </div>
                                </motion.div>
                            )}
                        </AnimatePresence>
                    </div>

                    <div className="pt-10 flex items-center gap-4">
                        {step !== "identity" && (
                            <Button
                                onClick={() => setStep(step === "review" ? "content" : step === "content" ? "style" : "identity")}
                                variant="outline"
                                className="h-14 px-8 rounded-2xl border-white/10 bg-transparent text-white font-black text-xs hover:bg-white/5"
                            >
                                BACK
                            </Button>
                        )}
                        {step !== "review" ? (
                            <Button
                                onClick={() => setStep(step === "identity" ? "style" : step === "style" ? "content" : "review")}
                                className="flex-1 h-14 rounded-2xl bg-white text-black font-black text-xs hover:bg-blue-600 hover:text-white transition-all group"
                            >
                                CONTINUE <ArrowRight className="ml-2 w-4 h-4 group-hover:translate-x-1 transition-transform" />
                            </Button>
                        ) : (
                            <Button
                                onClick={handleGenerate}
                                disabled={isGenerating || isComplete}
                                className="flex-1 h-14 rounded-2xl bg-blue-600 text-white font-black text-xs hover:bg-blue-700 transition-all shadow-xl shadow-blue-600/20"
                            >
                                {isGenerating ? "OPTIMIZING PIPELINE..." : isComplete ? "LAUNCHED" : (
                                    <><Wand2 className="mr-2 w-4 h-4" /> GENERATE PORTFOLIO</>
                                )}
                            </Button>
                        )}
                    </div>
                </div>

                {/* Right: Live Preview Panel */}
                <div className="hidden lg:block relative flex-1 bg-[#050508] p-12 overflow-hidden">
                    <div className="absolute top-0 right-0 p-12 opacity-5"><Layers className="w-48 h-48 rotate-12" /></div>

                    <div className="h-full flex flex-col">
                        <div className="flex items-center justify-between mb-8 pb-8 border-b border-white/5 text-[10px] font-black uppercase tracking-[0.2em] text-[#404040]">
                            <span>Real-time Preview Simulation</span>
                            <div className="flex gap-4">
                                <Monitor className="w-4 h-4 text-white" />
                                <Tablet className="w-4 h-4" />
                                <Smartphone className="w-4 h-4" />
                            </div>
                        </div>

                        <div className="flex-1 relative">
                            <AnimatePresence mode="wait">
                                {isGenerating ? (
                                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} className="absolute inset-0 flex flex-col items-center justify-center text-center">
                                        <div className="w-32 h-32 relative mb-12">
                                            <motion.div
                                                animate={{ rotate: 360 }}
                                                transition={{ duration: 4, repeat: Infinity, ease: "linear" }}
                                                className="absolute inset-0 rounded-[2rem] border-2 border-dashed border-blue-500/30"
                                            />
                                            <motion.div
                                                animate={{ rotate: -360 }}
                                                transition={{ duration: 2.5, repeat: Infinity, ease: "linear" }}
                                                className="absolute inset-4 rounded-full border-2 border-double border-white/10"
                                            />
                                            <div className="absolute inset-0 flex items-center justify-center"><Zap className="w-8 h-8 text-blue-500 animate-pulse" /></div>
                                        </div>
                                        <h3 className="text-xl font-black text-white mb-4">Nexus Engine is Active</h3>
                                        <p className="text-slate-500 text-xs font-medium max-w-xs leading-relaxed">Assembling high-fidelity components, optimizing texture maps, and injecting custom typography...</p>
                                    </motion.div>
                                ) : isComplete ? (
                                    <motion.div initial={{ opacity: 0, scale: 0.95 }} animate={{ opacity: 1, scale: 1 }} className="h-full flex flex-col items-center justify-center">
                                        <div className="p-1 w-full max-w-2xl bg-gradient-to-tr from-blue-600 via-purple-600 to-indigo-600 rounded-[3rem] shadow-2xl shadow-blue-600/20">
                                            <div className="bg-[#050510] h-[500px] rounded-[2.9rem] p-10 flex flex-col items-center justify-center text-center overflow-hidden relative">
                                                <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-transparent via-blue-500 to-transparent opacity-30" />
                                                <div className="w-20 h-20 rounded-full bg-slate-800 border border-white/10 mb-8 flex items-center justify-center font-black text-2xl">AK</div>
                                                <h1 className="text-4xl font-black text-white mb-4 uppercase tracking-tighter italic">I AM {name || "ADNAN KHAN"}</h1>
                                                <p className="text-slate-500 text-sm uppercase tracking-[0.4em] font-black">{role || "SYSTEM ARCHITECT"}</p>

                                                <div className="mt-12 grid grid-cols-2 gap-4 w-full px-12">
                                                    <div className="h-2 rounded-full bg-blue-600/20 border border-blue-500/10" />
                                                    <div className="h-2 rounded-full bg-blue-600/20 border border-blue-500/10" />
                                                    <div className="h-2 rounded-full bg-blue-600/20 border border-blue-500/10" />
                                                    <div className="h-2 rounded-full bg-blue-600/20 border border-blue-500/10" />
                                                </div>

                                                <Button className="mt-12 rounded-full bg-white text-black font-black text-[10px] px-10 h-12 uppercase tracking-widest shadow-xl">Explore Showcase</Button>
                                            </div>
                                        </div>
                                        <p className="mt-8 text-[10px] font-black uppercase tracking-[0.4em] text-emerald-500">Portfolio Compiled Successfully</p>
                                    </motion.div>
                                ) : (
                                    <motion.div initial={{ opacity: 0, y: 10 }} animate={{ opacity: 1, y: 0 }} className="h-full rounded-[3.5rem] bg-[#0A0A15] border border-white/5 p-12 relative overflow-hidden flex flex-col items-center justify-center text-center">
                                        <div className="absolute inset-0 opacity-10 pointer-events-none">
                                            <div className="absolute top-1/4 left-1/4 w-[400px] h-[400px] bg-blue-600 blur-[100px] rounded-full" />
                                            <div className="absolute bottom-1/4 right-1/4 w-[400px] h-[400px] bg-purple-600 blur-[100px] rounded-full" />
                                        </div>

                                        <div className="relative">
                                            <div className="w-16 h-16 rounded-2xl bg-white/5 border border-white/10 flex items-center justify-center mb-8 mx-auto"><Monitor className="w-6 h-6 text-slate-400" /></div>
                                            <h3 className="text-2xl font-black text-white mb-4 italic uppercase tracking-tighter">Blueprint Active.</h3>
                                            <p className="text-slate-500 text-xs font-medium max-w-xs leading-relaxed mx-auto">Complete the generator manual on the left to see your high-fidelity portfolio evolve in real-time.</p>
                                        </div>
                                    </motion.div>
                                )}
                            </AnimatePresence>
                        </div>

                        <div className="mt-12 flex items-center justify-between text-[10px] font-black uppercase tracking-[0.2em] text-[#202020]">
                            <span>Engine Status: Listen</span>
                            <span>Latency: 1.2ms</span>
                            <span>Buffer: Flush</span>
                        </div>
                    </div>
                </div>
            </main>
        </div>
    );
}

"use client";

import { useState, useMemo, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import {
    BookOpen,
    Search,
    Bookmark,
    Share2,
    Clock,
    Eye,
    ArrowRight,
    ChevronRight,
    TrendingUp,
    Mail,
    Feather,
    Menu,
    X,
    ArrowLeft,
    User,
    Hash
} from "lucide-react";
import Link from "next/link";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";

// --- Mock Data ---
const POSTS = [
    {
        id: 1,
        title: "The Future of Hyper-Personalized AI Interfaces",
        excerpt: "Exploring how generative design and user-centric LLMs are reshaping the way we interact with digital machinery.",
        author: "Adnan Khan",
        date: "Feb 14, 2026",
        category: "AI & Design",
        readTime: "8 min",
        image: "https://images.unsplash.com/photo-1677442136019-21780ecad995?auto=format&fit=crop&q=80&w=800",
        featured: true
    },
    {
        id: 2,
        title: "Architecting Scalable Micro-Frontends in 2026",
        excerpt: "Why the industry is moving back to modularity and how 'Nexus-Core' is leading the transition.",
        author: "Adnan Khan",
        date: "Feb 12, 2026",
        category: "Engineering",
        readTime: "12 min",
        image: "https://images.unsplash.com/photo-1451187580459-43490279c0fa?auto=format&fit=crop&q=80&w=800",
        featured: false
    },
    {
        id: 3,
        title: "The Death of Static Typography",
        excerpt: "Fluid type scales and variable weighting are no longer optionalthey are the soul of the modern web.",
        author: "Adnan Khan",
        date: "Feb 10, 2026",
        category: "Typography",
        readTime: "5 min",
        image: "https://images.unsplash.com/photo-1561070791-2526d30994b5?auto=format&fit=crop&q=80&w=800",
        featured: false
    },
    {
        id: 4,
        title: "Quantum State Management with React 20",
        excerpt: "A deep dive into the new concurrent rendering patterns and how they eliminate traditional boilerplate.",
        author: "Adnan Khan",
        date: "Feb 08, 2026",
        category: "Development",
        readTime: "15 min",
        image: "https://images.unsplash.com/photo-1633356122544-f134324a6cee?auto=format&fit=crop&q=80&w=800",
        featured: false
    }
];

const CATEGORIES = ["All", "AI & Design", "Engineering", "Typography", "Development", "Cybersecurity"];

export default function BlogCMS() {
    const [activeCategory, setActiveCategory] = useState("All");
    const [searchQuery, setSearchQuery] = useState("");
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [mounted, setMounted] = useState(false);

    useEffect(() => {
        const timer = setTimeout(() => {
            setMounted(true);
        }, 0);
        return () => clearTimeout(timer);
    }, []);

    const filteredPosts = useMemo(() => {
        return POSTS.filter(p => {
            const matchesCategory = activeCategory === "All" || p.category === activeCategory;
            const matchesSearch = p.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                p.excerpt.toLowerCase().includes(searchQuery.toLowerCase());
            return matchesCategory && matchesSearch;
        });
    }, [activeCategory, searchQuery]);

    if (!mounted) return null;

    return (
        <div className="min-h-screen bg-[#020205] text-[#E0E0E0] font-sans selection:bg-blue-500/30">
            {/* --- Sticky Header --- */}
            <header className="fixed top-0 left-0 right-0 z-50 bg-[#020205]/80 backdrop-blur-xl border-b border-white/5">
                <div className="container mx-auto px-6 h-20 flex items-center justify-between">
                    <Link href="/projects/blog-cms" className="text-2xl font-black tracking-tighter text-white flex items-center gap-2 group">
                        <Feather className="w-6 h-6 text-blue-500 group-hover:rotate-12 transition-transform" />
                        INSIGHT<span className="text-blue-500">.</span>
                    </Link>

                    <nav className="hidden lg:flex items-center gap-10">
                        {["Articles", "Series", "Archive", "Newsletter"].map(item => (
                            <Link key={item} href="#" className="text-xs font-black uppercase tracking-[0.2em] text-slate-500 hover:text-white transition-colors">
                                {item}
                            </Link>
                        ))}
                    </nav>

                    <div className="flex items-center gap-6">
                        <button className="text-slate-500 hover:text-white"><Search className="w-5 h-5" /></button>
                        <div className="h-6 w-[1px] bg-white/10 hidden sm:block" />
                        <Button size="sm" className="hidden sm:flex rounded-full bg-blue-600 font-bold hover:bg-blue-700 h-9 px-6 text-[10px] tracking-widest uppercase">
                            Subscribe
                        </Button>
                        <button className="lg:hidden" onClick={() => setIsMenuOpen(!isMenuOpen)}>
                            {isMenuOpen ? <X /> : <Menu />}
                        </button>
                    </div>
                </div>
            </header>

            <main className="pt-32 pb-24">
                <div className="container mx-auto px-6 lg:px-12">

                    {/* --- Featured Highlight --- */}
                    {activeCategory === "All" && POSTS.slice(0, 1).map(post => (
                        <motion.section
                            key={post.id}
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            className="mb-24 relative overflow-hidden rounded-[4rem] bg-[#0A0A1F] border border-white/5 group"
                        >
                            <div className="grid grid-cols-1 lg:grid-cols-2">
                                <div className="p-12 lg:p-24 flex flex-col justify-center">
                                    <span className="text-[10px] font-black uppercase tracking-[0.4em] text-blue-500 mb-6 block">Featured Analysis</span>
                                    <h1 className="text-4xl md:text-6xl font-black text-white mb-8 leading-[0.9] tracking-tighter">
                                        {post.title}
                                    </h1>
                                    <p className="text-slate-400 text-lg md:text-xl leading-relaxed mb-12 max-w-lg">
                                        {post.excerpt}
                                    </p>
                                    <div className="flex items-center gap-6">
                                        <Button className="h-14 px-10 rounded-full bg-white text-black font-black text-xs hover:bg-blue-600 hover:text-white transition-all">
                                            READ ARTICLE <ArrowRight className="ml-2 w-4 h-4" />
                                        </Button>
                                        <div className="flex items-center gap-3">
                                            <div className="w-10 h-10 rounded-full bg-slate-800 flex items-center justify-center font-bold text-xs">AK</div>
                                            <span className="text-xs font-bold text-slate-500">{post.readTime} reading</span>
                                        </div>
                                    </div>
                                </div>
                                <div className="relative aspect-[4/3] lg:aspect-auto overflow-hidden">
                                    <img src={post.image} className="w-full h-full object-cover grayscale opacity-60 group-hover:grayscale-0 group-hover:scale-105 transition-all duration-700" />
                                    <div className="absolute inset-y-0 left-0 w-32 bg-gradient-to-r from-[#0A0A1F] to-transparent hidden lg:block" />
                                </div>
                            </div>
                        </motion.section>
                    ))}

                    {/* --- Categories & Search Bar --- */}
                    <div className="flex flex-col md:flex-row md:items-center justify-between gap-8 mb-16 border-b border-white/5 pb-10">
                        <div className="flex items-center gap-3 overflow-x-auto scrollbar-hide pb-4 md:pb-0">
                            {CATEGORIES.map(cat => (
                                <button
                                    key={cat}
                                    onClick={() => setActiveCategory(cat)}
                                    className={cn(
                                        "px-6 h-10 rounded-full text-[10px] font-black uppercase tracking-widest transition-all",
                                        activeCategory === cat
                                            ? "bg-blue-600 text-white"
                                            : "bg-white/5 text-slate-500 border border-white/5 hover:border-blue-500/30"
                                    )}
                                >
                                    {cat}
                                </button>
                            ))}
                        </div>

                        <div className="relative w-full md:w-80">
                            <Search className="absolute left-4 top-1/2 -translate-y-1/2 w-4 h-4 text-slate-600" />
                            <input
                                type="text"
                                placeholder="Filter by title..."
                                className="w-full h-12 bg-white/2 border border-white/5 rounded-2xl pl-12 pr-4 text-xs font-bold outline-none focus:border-blue-500/50 transition-all placeholder:text-slate-700"
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                            />
                        </div>
                    </div>

                    {/* --- Main Grid --- */}
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-12">
                        <AnimatePresence mode="popLayout">
                            {filteredPosts.map((post, idx) => (
                                <motion.article
                                    layout
                                    key={post.id}
                                    initial={{ opacity: 0, scale: 0.95 }}
                                    animate={{ opacity: 1, scale: 1 }}
                                    exit={{ opacity: 0, scale: 0.95 }}
                                    transition={{ delay: idx * 0.05 }}
                                    className="group flex flex-col"
                                >
                                    <Link href="#" className="relative aspect-video rounded-[2.5rem] overflow-hidden mb-8 border border-white/5 block">
                                        <img src={post.image} className="w-full h-full object-cover transition-transform duration-700 group-hover:scale-110" />
                                        <div className="absolute top-6 left-6 px-3 py-1 bg-black/60 backdrop-blur-md rounded-full text-[9px] font-black uppercase tracking-widest text-white border border-white/10">
                                            {post.category}
                                        </div>
                                    </Link>

                                    <div className="px-2 flex-1 flex flex-col">
                                        <div className="flex items-center gap-3 text-[10px] font-black uppercase tracking-[0.2em] text-slate-500 mb-4">
                                            <span>{post.date}</span>
                                            <span className="w-1.5 h-1.5 rounded-full bg-blue-500" />
                                            <span>{post.readTime}</span>
                                        </div>
                                        <Link href="#">
                                            <h3 className="text-2xl font-bold text-white mb-4 leading-tight group-hover:text-blue-500 transition-colors">
                                                {post.title}
                                            </h3>
                                        </Link>
                                        <p className="text-slate-500 text-sm leading-relaxed mb-8 line-clamp-3">
                                            {post.excerpt}
                                        </p>

                                        <div className="mt-auto flex items-center justify-between pt-6 border-t border-white/5">
                                            <div className="flex items-center gap-3">
                                                <div className="w-8 h-8 rounded-full bg-slate-800 border border-white/10 flex items-center justify-center font-bold text-[10px]">AK</div>
                                                <span className="text-xs font-bold text-slate-400">Adnan Khan</span>
                                            </div>
                                            <button className="p-2 text-slate-600 hover:text-white transition-colors">
                                                <Bookmark className="w-4 h-4" />
                                            </button>
                                        </div>
                                    </div>
                                </motion.article>
                            ))}
                        </AnimatePresence>
                    </div>

                    {/* --- Newsletter / CTA --- */}
                    <section className="mt-32 p-12 lg:p-24 rounded-[4rem] bg-gradient-to-br from-blue-600/10 to-transparent border border-white/5 text-center relative overflow-hidden">
                        <div className="absolute top-0 left-0 w-full h-full pointer-events-none">
                            <TrendingUp className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-[800px] h-[800px] text-blue-500/5 rotate-12" />
                        </div>

                        <div className="relative z-10 max-w-2xl mx-auto">
