                setUser(user);
            }
            setLoading(false);
        };
        checkUser();
    }, [router]);

    const handleSignOut = async () => {
        await supabase.auth.signOut();
        router.push("/projects/e-commerce");
    };

    if (loading) {
        return (
            <div className="min-h-screen bg-[#020205] flex items-center justify-center">
