                            </div>
                            <div className="pt-4 border-t border-white/5">
                                <p className="text-xs text-slate-500 mb-1">Member Since</p>
                                <p className="text-sm font-bold">
                                    {new Date(user.created_at).toLocaleDateString("en-US", {
                                        month: "long",
                                        year: "numeric",
                                    })}
                                </p>
                            </div>
                        </div>
                    </div>
                </div>
            </main>
        </div>
    );
}

"use client";

import { useState, useEffect } from "react";
import Link from "next/link";
import { useRouter } from "next/navigation";
import { motion } from "framer-motion";
import { ArrowLeft, Save, Eye, EyeOff } from "lucide-react";
import { Button } from "@/components/ui/button";
import { supabase } from "@/lib/supabase";
import { cn } from "@/lib/utils";

export default function ProfileSettingsPage() {
    const router = useRouter();
    const [user, setUser] = useState<any>(null);
    const [loading, setLoading] = useState(true);
    const [saving, setSaving] = useState(false);
    const [showCurrentPassword, setShowCurrentPassword] = useState(false);
    const [showNewPassword, setShowNewPassword] = useState(false);

    // Form fields
    const [fullName, setFullName] = useState("");
    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState("");
    const [currentPassword, setCurrentPassword] = useState("");
    const [newPassword, setNewPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");

    useEffect(() => {
        checkUser();
    }, []);

    const checkUser = async () => {
        const { data: { user } } = await supabase.auth.getUser();
        if (!user) {
            router.push("/projects/e-commerce/auth/login");
        } else {
            setUser(user);
            setFullName(user.user_metadata?.full_name || "");
            setEmail(user.email || "");
            setPhone(user.user_metadata?.phone || "");
        }
        setLoading(false);
    };

    const handleSaveProfile = async () => {
        setSaving(true);
        try {
            const { error } = await supabase.auth.updateUser({
                data: { full_name: fullName, phone }
            });

            if (error) throw error;
            alert("Profile updated successfully!");
        } catch (error) {
            console.error(error);
            alert("Failed to update profile");
        } finally {
            setSaving(false);
        }
    };

    const handleChangePassword = async () => {
        if (newPassword !== confirmPassword) {
            alert("Passwords do not match");
            return;
        }

        if (newPassword.length < 6) {
            alert("Password must be at least 6 characters");
            return;
        }

        setSaving(true);
        try {
            const { error } = await supabase.auth.updateUser({
                password: newPassword
            });

            if (error) throw error;
            alert("Password updated successfully!");
            setCurrentPassword("");
            setNewPassword("");
            setConfirmPassword("");
        } catch (error) {
            console.error(error);
            alert("Failed to update password");
        } finally {
            setSaving(false);
        }
    };

    if (loading) {
        return (
            <div className="min-h-screen bg-[#020205] flex items-center justify-center">
                <div className="text-white">Loading...</div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-[#020205] text-[#E0E0E0]">
            <div className="container mx-auto px-6 py-12">
                <Link
                    href="/projects/e-commerce/account"
                    className="inline-flex items-center gap-2 text-slate-500 hover:text-white mb-8 transition-colors"
                >
                    <ArrowLeft className="w-4 h-4" />
                    Back to Account
                </Link>

                <div className="mb-12">
                    <h1 className="text-4xl md:text-5xl font-black mb-3">Profile Settings</h1>
                    <p className="text-slate-500">Manage your account information and preferences</p>
                </div>

                <div className="max-w-2xl space-y-8">
                    {/* Personal Information */}
                    <motion.div
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        className="p-8 rounded-3xl bg-white/5 border border-white/10"
                    >
                        <h2 className="text-2xl font-black mb-6">Personal Information</h2>
                        <div className="space-y-6">
                            <div>
                                <label className="block text-sm font-bold mb-2">Full Name</label>
                                <input
                                    type="text"
                                    value={fullName}
                                    onChange={(e) => setFullName(e.target.value)}
                                    className="w-full h-14 bg-white/5 border border-white/10 rounded-xl px-4 text-sm focus:border-white/30 focus:outline-none"
                                    placeholder="Enter your full name"
                                />
                            </div>

                            <div>
                                <label className="block text-sm font-bold mb-2">Email Address</label>
                                <input
                                    type="email"
                                    value={email}
                                    disabled
                                    className="w-full h-14 bg-white/5 border border-white/10 rounded-xl px-4 text-sm opacity-50 cursor-not-allowed"
                                />
                                <p className="text-xs text-slate-500 mt-2">
                                    Contact support to change your email address
                                </p>
                            </div>

                            <div>
                                <label className="block text-sm font-bold mb-2">Phone Number (Optional)</label>
                                <input
                                    type="tel"
                                    value={phone}
                                    onChange={(e) => setPhone(e.target.value)}
                                    className="w-full h-14 bg-white/5 border border-white/10 rounded-xl px-4 text-sm focus:border-white/30 focus:outline-none"
                                    placeholder="+1 (555) 123-4567"
                                />
                            </div>

                            <Button
                                onClick={handleSaveProfile}
                                disabled={saving}
                                className="h-14 px-8 rounded-xl bg-white text-black font-black hover:bg-white/90"
                            >
                                <Save className="w-4 h-4 mr-2" />
                                {saving ? "Saving..." : "Save Changes"}
                            </Button>
                        </div>
                    </motion.div>

                    {/* Change Password */}
                    <motion.div
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.1 }}
                        className="p-8 rounded-3xl bg-white/5 border border-white/10"
                    >
                        <h2 className="text-2xl font-black mb-6">Change Password</h2>
                        <div className="space-y-6">
                            <div>
                                <label className="block text-sm font-bold mb-2">Current Password</label>
                                <div className="relative">
                                    <input
                                        type={showCurrentPassword ? "text" : "password"}
                                        value={currentPassword}
                                        onChange={(e) => setCurrentPassword(e.target.value)}
                                        className="w-full h-14 bg-white/5 border border-white/10 rounded-xl px-4 pr-12 text-sm focus:border-white/30 focus:outline-none"
                                        placeholder="Enter current password"
                                    />
                                    <button
                                        type="button"
                                        onClick={() => setShowCurrentPassword(!showCurrentPassword)}
                                        className="absolute right-4 top-1/2 -translate-y-1/2 text-slate-500 hover:text-white"
                                    >
                                        {showCurrentPassword ? <EyeOff className="w-5 h-5" /> : <Eye className="w-5 h-5" />}
                                    </button>
                                </div>
                            </div>

                            <div>
                                <label className="block text-sm font-bold mb-2">New Password</label>
                                <div className="relative">
                                    <input
                                        type={showNewPassword ? "text" : "password"}
                                        value={newPassword}
                                        onChange={(e) => setNewPassword(e.target.value)}
                                        className="w-full h-14 bg-white/5 border border-white/10 rounded-xl px-4 pr-12 text-sm focus:border-white/30 focus:outline-none"
                                        placeholder="Enter new password"
                                    />
                                    <button
                                        type="button"
                                        onClick={() => setShowNewPassword(!showNewPassword)}
                                        className="absolute right-4 top-1/2 -translate-y-1/2 text-slate-500 hover:text-white"
                                    >
                                        {showNewPassword ? <EyeOff className="w-5 h-5" /> : <Eye className="w-5 h-5" />}
                                    </button>
                                </div>
                            </div>

                            <div>
                                <label className="block text-sm font-bold mb-2">Confirm New Password</label>
                                <input
                                    type="password"
                                    value={confirmPassword}
                                    onChange={(e) => setConfirmPassword(e.target.value)}
                                    className="w-full h-14 bg-white/5 border border-white/10 rounded-xl px-4 text-sm focus:border-white/30 focus:outline-none"
                                    placeholder="Confirm new password"
                                />
                            </div>

                            <Button
                                onClick={handleChangePassword}
                                disabled={saving || !newPassword || !confirmPassword}
                                className="h-14 px-8 rounded-xl bg-white text-black font-black hover:bg-white/90 disabled:opacity-50"
                            >
                                <Save className="w-4 h-4 mr-2" />
                                {saving ? "Updating..." : "Update Password"}
                            </Button>
                        </div>
                    </motion.div>

                    {/* Danger Zone */}
                    <motion.div
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.2 }}
                        className="p-8 rounded-3xl bg-red-500/5 border border-red-500/20"
                    >
                        <h2 className="text-2xl font-black mb-3 text-red-500">Danger Zone</h2>
                        <p className="text-sm text-slate-400 mb-6">
                            Once you delete your account, there is no going back. Please be certain.
                        </p>
                        <Button
                            variant="outline"
                            className="h-12 px-6 rounded-xl border-red-500/50 bg-red-500/10 text-red-500 hover:bg-red-500/20 font-black"
                        >
                            Delete Account
                        </Button>
                    </motion.div>
                </div>
            </div>
        </div>
    );
}

"use client";

import { useState, useEffect } from "react";
import Link from "next/link";
import { useRouter } from "next/navigation";
import { motion } from "framer-motion";
import { Heart, ShoppingCart, Trash2, ArrowLeft } from "lucide-react";
import { Button } from "@/components/ui/button";
import { supabase } from "@/lib/supabase";
import { useCartStore, PRODUCTS } from "../../store";
import { cn } from "@/lib/utils";
import { User as AuthUser } from "@supabase/supabase-js";

export default function WishlistPage() {
    const router = useRouter();
    const [user, setUser] = useState<AuthUser | null>(null);
    const { wishlist, toggleWishlist, addToCart } = useCartStore((state: any) => ({
        wishlist: state.wishlist,
        toggleWishlist: state.toggleWishlist,
        addToCart: state.addToCart
    }));

    useEffect(() => {
        const checkUser = async () => {
            const { data: { user } } = await supabase.auth.getUser();
            if (!user) {
                router.push("/projects/e-commerce/auth/login");
            } else {
                setUser(user);
            }
        };
        checkUser();
    }, [router]);

    const wishlistProducts = PRODUCTS.filter((p) => wishlist.includes(p.id));

    const handleAddToCart = (productId: number) => {
        addToCart(productId);
        // Optional: show toast notification
    };

    if (!user) {
        return (
            <div className="min-h-screen bg-[#020205] flex items-center justify-center">
                <div className="text-white">Loading...</div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-[#020205] text-[#E0E0E0]">
            <div className="container mx-auto px-6 py-12">
                <Link
                    href="/projects/e-commerce/account"
                    className="inline-flex items-center gap-2 text-slate-500 hover:text-white mb-8 transition-colors"
                >
                    <ArrowLeft className="w-4 h-4" />
                    Back to Account
                </Link>

                <div className="mb-12">
                    <h1 className="text-4xl md:text-5xl font-black mb-3">My Wishlist</h1>
                    <p className="text-slate-500">
                        {wishlistProducts.length} {wishlistProducts.length === 1 ? "item" : "items"} saved
                    </p>
                </div>

                {wishlistProducts.length === 0 ? (
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        className="text-center py-20 rounded-3xl border-2 border-dashed border-white/10"
                    >
                        <Heart className="w-16 h-16 mx-auto mb-6 text-slate-700" />
                        <h3 className="text-2xl font-black mb-2">Your wishlist is empty</h3>
                        <p className="text-slate-500 mb-8">Save your favorite items for later</p>
                        <Button
                            onClick={() => router.push("/projects/e-commerce")}
                            className="h-12 px-8 rounded-xl bg-white text-black font-black hover:bg-white/90"
                        >
                            Continue Shopping
                        </Button>
                    </motion.div>
                ) : (
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                        {wishlistProducts.map((product) => (
                            <motion.div
                                key={product.id}
                                layout
                                initial={{ opacity: 0, scale: 0.95 }}
                                animate={{ opacity: 1, scale: 1 }}
                                exit={{ opacity: 0, scale: 0.95 }}
                                className="group relative rounded-2xl bg-white/5 border border-white/10 overflow-hidden hover:border-white/20 transition-colors"
                            >
                                <div className="aspect-square bg-white/5 overflow-hidden">
                                    <img
                                        src={product.image}
                                        alt={product.name}
                                        className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-500"
                                    />
                                </div>

                                <div className="p-6">
                                    <div className="mb-4">
                                        <h3 className="font-black text-lg mb-2">{product.name}</h3>
                                        <p className="text-slate-500 text-sm line-clamp-2">{product.description}</p>
                                    </div>

                                    <div className="flex items-center justify-between mb-4">
                                        <div>
                                            {product.salePrice ? (
                                                <div className="flex items-center gap-2">
                                                    <span className="text-2xl font-black">${product.salePrice.toFixed(2)}</span>
                                                    <span className="text-sm text-slate-500 line-through">${product.price.toFixed(2)}</span>
                                                </div>
                                            ) : (
                                                <span className="text-2xl font-black">${product.price.toFixed(2)}</span>
                                            )}
                                        </div>
                                        {product.stock < 10 && product.stock > 0 && (
                                            <span className="text-xs text-amber-500 font-bold">Only {product.stock} left</span>
                                        )}
                                    </div>

                                    <div className="flex gap-2">
                                        <Button
                                            onClick={() => handleAddToCart(product.id)}
                                            disabled={product.stock === 0}
                                            className="flex-1 h-11 rounded-xl bg-white text-black font-black text-xs hover:bg-white/90 disabled:opacity-50 disabled:cursor-not-allowed"
                                        >
                                            <ShoppingCart className="w-4 h-4 mr-2" />
                                            {product.stock === 0 ? "OUT OF STOCK" : "ADD TO CART"}
                                        </Button>
                                        <Button
                                            onClick={() => toggleWishlist(product.id)}
                                            variant="outline"
                                            className="h-11 px-4 rounded-xl border-white/10 bg-white/5 hover:bg-red-500/10 hover:border-red-500/20"
                                        >
                                            <Trash2 className="w-4 h-4 text-red-500" />
                                        </Button>
                                    </div>
                                </div>

                                {product.stock === 0 && (
                                    <div className="absolute inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center">
                                        <span className="text-xl font-black">OUT OF STOCK</span>
                                    </div>
                                )}
                            </motion.div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
}

"use client";

import { useState, useEffect } from "react";
import Link from "next/link";
import { useRouter } from "next/navigation";
import { motion } from "framer-motion";
import {
    LayoutDashboard,
    Package,
    ShoppingCart,
    Users,
    Settings,
    TrendingUp,
    DollarSign,
    AlertCircle,
    BarChart3,
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { supabase } from "@/lib/supabase";
import { PRODUCTS } from "../store";
import { cn } from "@/lib/utils";
import { User as AuthUser } from "@supabase/supabase-js";

export default function AdminDashboard() {
    const router = useRouter();
    const [user, setUser] = useState<AuthUser | null>(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const checkAdmin = async () => {
            const { data: { user } } = await supabase.auth.getUser();
            // In a real app, check if user has admin role
            if (!user) {
                router.push("/projects/e-commerce/auth/login");
            } else {
                setUser(user);
            }
            setLoading(false);
        };
        checkAdmin();
    }, [router]);

    if (loading) {
        return (
            <div className="min-h-screen bg-[#020205] flex items-center justify-center">
                <div className="text-white">Loading...</div>
            </div>
        );
    }

    const stats = [
        {
            label: "Total Revenue",
            value: "$12,450",
            change: "+12.5%",
            icon: DollarSign,
            trend: "up",
        },
        {
            label: "Total Orders",
            value: "156",
            change: "+8.2%",
            icon: ShoppingCart,
            trend: "up",
        },
        {
            label: "Products",
            value: PRODUCTS.length.toString(),
            change: "+2 this week",
            icon: Package,
            trend: "up",
        },
        {
            label: "Low Stock Alerts",
            value: "3",
            change: "Needs attention",
            icon: AlertCircle,
            trend: "warning",
        },
    ];

    const recentOrders = [
        { id: "ORD-156", customer: "Sarah Chen", total: 549.98, status: "processing", time: "2 min ago" },
        { id: "ORD-155", customer: "Mike Johnson", total: 299.00, status: "shipped", time: "1 hour ago" },
        { id: "ORD-154", customer: "Emma Wilson", total: 159.00, status: "delivered", time: "3 hours ago" },
    ];

    const lowStockProducts = PRODUCTS.filter((p) => p.stock < 10);

    return (
        <div className="min-h-screen bg-[#020205] text-[#E0E0E0]">
            {/* Sidebar */}
            <div className="fixed left-0 top-0 h-full w-64 bg-white/5 border-r border-white/10 p-6 hidden lg:block">
                <div className="mb-12">
                    <h2 className="text-2xl font-black tracking-tighter italic">ZENITH</h2>
                    <p className="text-xs text-slate-500 mt-1">Admin Panel</p>
                </div>

                <nav className="space-y-2">
                    {[
                        { href: "/projects/e-commerce/admin", icon: LayoutDashboard, label: "Dashboard", active: true },
                        { href: "/projects/e-commerce/admin/products", icon: Package, label: "Products" },
                        { href: "/projects/e-commerce/admin/orders", icon: ShoppingCart, label: "Orders" },
                        { href: "/projects/e-commerce/admin/customers", icon: Users, label: "Customers" },
                        { href: "/projects/e-commerce/admin/analytics", icon: BarChart3, label: "Analytics" },
                        { href: "/projects/e-commerce/admin/settings", icon: Settings, label: "Settings" },
                    ].map((item) => (
                        <Link
                            key={item.href}
                            href={item.href}
                            className={cn(
                                "flex items-center gap-3 px-4 py-3 rounded-xl transition-colors",
                                item.active
                                    ? "bg-white/10 text-white"
                                    : "text-slate-500 hover:bg-white/5 hover:text-white"
                            )}
                        >
                            <item.icon className="w-5 h-5" />
                            <span className="font-semibold text-sm">{item.label}</span>
                        </Link>
                    ))}
                </nav>

                <div className="absolute bottom-6 left-6 right-6">
                    <Link
                        href="/projects/e-commerce"
                        className="flex items-center justify-center gap-2 p-3 rounded-xl bg-white/5 hover:bg-white/10 transition-colors"
                    >
                        <span className="text-sm font-semibold">View Store</span>
                    </Link>
                </div>
            </div>

            {/* Main Content */}
            <div className="lg:ml-64">
                <div className="container mx-auto px-6 py-12">
                    <div className="mb-12">
                        <h1 className="text-4xl md:text-5xl font-black mb-3">Dashboard</h1>
                        <p className="text-slate-500">Welcome back, Admin</p>
                    </div>

                    {/* Stats Grid */}
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-12">
                        {stats.map((stat, i) => (
                            <motion.div
                                key={stat.label}
                                initial={{ opacity: 0, y: 20 }}
                                animate={{ opacity: 1, y: 0 }}
                                transition={{ delay: i * 0.1 }}
                                className="p-6 rounded-2xl bg-white/5 border border-white/10"
                            >
                                <div className="flex items-center justify-between mb-4">
                                    <stat.icon className={cn(
                                        "w-8 h-8",
                                        stat.trend === "warning" ? "text-amber-500" : "text-white/50"
                                    )} />
                                    <span
                                        className={cn(
                                            "text-xs font-bold px-3 py-1 rounded-full",
                                            stat.trend === "up"
                                                ? "bg-emerald-500/10 text-emerald-500"
                                                : "bg-amber-500/10 text-amber-500"
                                        )}
                                    >
                                        {stat.change}
                                    </span>
                                </div>
                                <p className="text-sm font-bold text-slate-500 mb-1">{stat.label}</p>
                                <p className="text-3xl font-black">{stat.value}</p>
                            </motion.div>
                        ))}
                    </div>

                    <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                        {/* Recent Orders */}
                        <div className="p-8 rounded-3xl bg-white/5 border border-white/10">
                            <div className="flex items-center justify-between mb-6">
                                <h2 className="text-2xl font-black">Recent Orders</h2>
                                <Link
                                    href="/projects/e-commerce/admin/orders"
                                    className="text-sm font-bold text-white hover:underline"
                                >
                                    View All
                                </Link>
                            </div>
                            <div className="space-y-4">
                                {recentOrders.map((order) => (
                                    <div
                                        key={order.id}
                                        className="p-4 rounded-xl bg-white/5 border border-white/5 hover:border-white/10 transition-colors"
                                    >
                                        <div className="flex items-center justify-between mb-2">
                                            <div>
                                                <span className="font-mono text-sm font-bold">{order.id}</span>
                                                <p className="text-xs text-slate-500 mt-1">{order.customer}</p>
                                            </div>
                                            <span
                                                className={cn(
                                                    "text-xs font-bold px-3 py-1 rounded-full",
                                                    order.status === "delivered"
                                                        ? "bg-emerald-500/10 text-emerald-500"
                                                        : order.status === "shipped"
                                                            ? "bg-blue-500/10 text-blue-500"
                                                            : "bg-amber-500/10 text-amber-500"
                                                )}
                                            >
                                                {order.status.toUpperCase()}
                                            </span>
                                        </div>
                                        <div className="flex items-center justify-between text-sm">
                                            <span className="text-slate-500">{order.time}</span>
                                            <span className="text-white font-bold">${order.total.toFixed(2)}</span>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>

                        {/* Low Stock Alerts */}
                        <div className="p-8 rounded-3xl bg-white/5 border border-white/10">
                            <div className="flex items-center justify-between mb-6">
                                <h2 className="text-2xl font-black">Low Stock Alerts</h2>
                                <Link
                                    href="/projects/e-commerce/admin/products"
                                    className="text-sm font-bold text-white hover:underline"
                                >
                                    Manage
                                </Link>
                            </div>
                            <div className="space-y-4">
                                {lowStockProducts.map((product) => (
                                    <div
                                        key={product.id}
                                        className="p-4 rounded-xl bg-amber-500/5 border border-amber-500/20"
                                    >
                                        <div className="flex items-center gap-4">
                                            <img
                                                src={product.image}
                                                alt={product.name}
                                                className="w-12 h-12 rounded-lg object-cover"
                                            />
                                            <div className="flex-1">
                                                <p className="font-bold text-sm">{product.name}</p>
                                                <p className="text-xs text-amber-500 font-bold mt-1">
                                                    Only {product.stock} left in stock
                                                </p>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

"use client";

import { useState } from "react";
import { useRouter } from "next/navigation";
import Link from "next/link";
import { motion } from "framer-motion";
import { Mail, Lock, ArrowRight, ArrowLeft, ShoppingBag, Eye, EyeOff } from "lucide-react";
import { Button } from "@/components/ui/button";
import { supabase } from "@/lib/supabase";
import { cn } from "@/lib/utils";

export default function LoginPage() {
    const router = useRouter();
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [rememberMe, setRememberMe] = useState(false);
    const [showPassword, setShowPassword] = useState(false);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setLoading(true);
        setError("");

        try {
            const { data, error: authError } = await supabase.auth.signInWithPassword({
                email,
                password,
            });

            if (authError) throw authError;

            // Redirect to account or back to shop
            router.push("/projects/e-commerce/account");
        } catch (err: any) {
            setError(err.message || "Failed to sign in");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-[#020205] text-[#E0E0E0] flex items-center justify-center p-6">
            <div className="w-full max-w-md">
                {/* Back Button */}
                <Link
                    href="/projects/e-commerce"
                    className="inline-flex items-center gap-2 text-slate-500 hover:text-white mb-8 transition-colors"
                >
                    <ArrowLeft className="w-4 h-4" />
                    Back to Shop
                </Link>

                {/* Logo */}
                <Link href="/projects/e-commerce" className="flex items-center gap-2 justify-center mb-12">
                    <ShoppingBag className="w-8 h-8" />
                    <span className="text-2xl font-black tracking-tighter italic">ZENITH.</span>
                </Link>

                {/* Auth Card */}
                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="bg-white/5 border border-white/10 rounded-3xl p-10 backdrop-blur-xl"
                >
                    <div className="mb-8">
                        <h1 className="text-3xl font-black mb-2">Welcome Back</h1>
                        <p className="text-slate-500 text-sm">Sign in to your account to continue</p>
                    </div>

                    {error && (
                        <div className="mb-6 p-4 bg-red-500/10 border border-red-500/20 rounded-xl text-red-500 text-sm">
                            {error}
                        </div>
                    )}

                    <form onSubmit={handleSubmit} className="space-y-5">
                        <div>
                            <label className="text-xs font-bold uppercase tracking-widest text-slate-500 mb-2 block">
                                Email Address
                            </label>
                            <div className="relative">
                                <Mail className="absolute left-4 top-1/2 -translate-y-1/2 w-4 h-4 text-slate-500" />
                                <input
                                    type="email"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    placeholder="you@example.com"
                                    required
                                    className="w-full h-14 bg-white/5 border border-white/10 rounded-xl pl-12 pr-4 text-sm focus:border-white/30 focus:outline-none transition-colors"
                                />
                            </div>
                        </div>

                        <div>
                            <label className="text-xs font-bold uppercase tracking-widest text-slate-500 mb-2 block">
                                Password
                            </label>
                            <div className="relative">
                                <Lock className="absolute left-4 top-1/2 -translate-y-1/2 w-4 h-4 text-slate-500" />
                                <input
                                    type={showPassword ? "text" : "password"}
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    placeholder=""
                                    required
                                    className="w-full h-14 bg-white/5 border border-white/10 rounded-xl pl-12 pr-12 text-sm focus:border-white/30 focus:outline-none transition-colors"
                                />
                                <button
                                    type="button"
                                    onClick={() => setShowPassword(!showPassword)}
                                    className="absolute right-4 top-1/2 -translate-y-1/2 text-slate-500 hover:text-white transition-colors"
                                >
                                    {showPassword ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                                </button>
                            </div>
                        </div>

                        <div className="flex items-center justify-between text-sm">
                            <label className="flex items-center gap-2 cursor-pointer">
                                <input
                                    type="checkbox"
                                    checked={rememberMe}
                                    onChange={(e) => setRememberMe(e.target.checked)}
                                    className="w-4 h-4 rounded border-white/10 bg-white/5 text-white focus:ring-white/20"
                                />
                                <span className="text-slate-400">Remember me</span>
                            </label>
                            <Link href="#" className="text-white hover:underline font-semibold">
                                Forgot password?
                            </Link>
                        </div>

                        <Button
                            type="submit"
                            disabled={loading}
                            className="w-full h-14 bg-white text-black font-black text-sm hover:bg-white/90 disabled:opacity-50 rounded-xl group"
                        >
                            {loading ? "SIGNING IN..." : (
                                <>
                                    SIGN IN <ArrowRight className="ml-2 w-4 h-4 group-hover:translate-x-1 transition-transform" />
                                </>
                            )}
                        </Button>
                    </form>

                    <div className="mt-8 text-center text-sm">
                        <span className="text-slate-500">Don&apos;t have an account? </span>
                        <Link href="/projects/e-commerce/auth/register" className="text-white font-bold hover:underline">
                            Create one
                        </Link>
                    </div>
                </motion.div>

                <div className="mt-8 text-center">
                    <Link href="/projects/e-commerce" className="text-slate-500 hover:text-white text-sm transition-colors">
                         Back to Store
                    </Link>
                </div>
            </div>
        </div>
    );
}

"use client";

import { useState } from "react";
import { useRouter } from "next/navigation";
import Link from "next/link";
import { motion } from "framer-motion";
import { ShoppingBag, Mail, Lock, User, ArrowLeft, ArrowRight, Eye, EyeOff } from "lucide-react";
import { Button } from "@/components/ui/button";
import { supabase } from "@/lib/supabase";

export default function RegisterPage() {
    const router = useRouter();
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        password: "",
        confirmPassword: "",
    });
    const [showPassword, setShowPassword] = useState(false);
    const [acceptTerms, setAcceptTerms] = useState(false);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setLoading(true);
        setError("");

        if (formData.password !== formData.confirmPassword) {
            setError("Passwords do not match");
            setLoading(false);
            return;
        }

        if (!acceptTerms) {
            setError("Please accept the terms and conditions");
            setLoading(false);
            return;
        }

        try {
            const { data, error: authError } = await supabase.auth.signUp({
                email: formData.email,
                password: formData.password,
                options: {
                    data: {
                        full_name: formData.name,
                    },
                },
            });

            if (authError) throw authError;

            //Auto-login and redirect
            router.push("/projects/e-commerce/account");
        } catch (err: any) {
            setError(err.message || "Failed to create account");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-[#020205] text-[#E0E0E0] flex items-center justify-center p-6">
            <div className="w-full max-w-md">
                {/* Back Button */}
                <Link
                    href="/projects/e-commerce"
                    className="inline-flex items-center gap-2 text-slate-500 hover:text-white mb-8 transition-colors"
                >
                    <ArrowLeft className="w-4 h-4" />
                    Back to Shop
                </Link>

                {/* Logo */}
                <Link href="/projects/e-commerce" className="flex items-center gap-2 justify-center mb-12">
                    <ShoppingBag className="w-8 h-8" />
                    <span className="text-2xl font-black tracking-tighter italic">ZENITH.</span>
                </Link>

                {/* Auth Card */}
                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="bg-white/5 border border-white/10 rounded-3xl p-10 backdrop-blur-xl"
                >
                    <div className="mb-8">
                        <h1 className="text-3xl font-black mb-2">Create Account</h1>
                        <p className="text-slate-500 text-sm">Join Zenith and start shopping</p>
                    </div>

                    {error && (
                        <div className="mb-6 p-4 bg-red-500/10 border border-red-500/20 rounded-xl text-red-500 text-sm">
                            {error}
                        </div>
                    )}

                    <form onSubmit={handleSubmit} className="space-y-5">
                        <div>
                            <label className="text-xs font-bold uppercase tracking-widest text-slate-500 mb-2 block">
                                Full Name
                            </label>
                            <div className="relative">
                                <User className="absolute left-4 top-1/2 -translate-y-1/2 w-4 h-4 text-slate-500" />
                                <input
                                    type="text"
                                    value={formData.name}
                                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                                    placeholder="John Doe"
                                    required
                                    className="w-full h-14 bg-white/5 border border-white/10 rounded-xl pl-12 pr-4 text-sm focus:border-white/30 focus:outline-none transition-colors"
                                />
                            </div>
                        </div>

                        <div>
                            <label className="text-xs font-bold uppercase tracking-widest text-slate-500 mb-2 block">
                                Email Address
                            </label>
                            <div className="relative">
                                <Mail className="absolute left-4 top-1/2 -translate-y-1/2 w-4 h-4 text-slate-500" />
                                <input
                                    type="email"
                                    value={formData.email}
                                    onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                                    placeholder="you@example.com"
                                    required
                                    className="w-full h-14 bg-white/5 border border-white/10 rounded-xl pl-12 pr-4 text-sm focus:border-white/30 focus:outline-none transition-colors"
                                />
                            </div>
                        </div>

                        <div>
                            <label className="text-xs font-bold uppercase tracking-widest text-slate-500 mb-2 block">
                                Password
                            </label>
                            <div className="relative">
                                <Lock className="absolute left-4 top-1/2 -translate-y-1/2 w-4 h-4 text-slate-500" />
                                <input
                                    type={showPassword ? "text" : "password"}
                                    value={formData.password}
                                    onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                                    placeholder=""
                                    required
                                    minLength={6}
                                    className="w-full h-14 bg-white/5 border border-white/10 rounded-xl pl-12 pr-12 text-sm focus:border-white/30 focus:outline-none transition-colors"
                                />
                                <button
                                    type="button"
                                    onClick={() => setShowPassword(!showPassword)}
                                    className="absolute right-4 top-1/2 -translate-y-1/2 text-slate-500 hover:text-white transition-colors"
                                >
                                    {showPassword ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                                </button>
                            </div>
                        </div>

                        <div>
                            <label className="text-xs font-bold uppercase tracking-widest text-slate-500 mb-2 block">
                                Confirm Password
                            </label>
                            <div className="relative">
                                <Lock className="absolute left-4 top-1/2 -translate-y-1/2 w-4 h-4 text-slate-500" />
                                <input
                                    type={showPassword ? "text" : "password"}
                                    value={formData.confirmPassword}
                                    onChange={(e) => setFormData({ ...formData, confirmPassword: e.target.value })}
                                    placeholder=""
                                    required
                                    className="w-full h-14 bg-white/5 border border-white/10 rounded-xl pl-12 pr-4 text-sm focus:border-white/30 focus:outline-none transition-colors"
                                />
                            </div>
                        </div>

                        <label className="flex items-start gap-3 cursor-pointer text-sm">
                            <input
                                type="checkbox"
                                checked={acceptTerms}
                                onChange={(e) => setAcceptTerms(e.target.checked)}
                                className="w-4 h-4 mt-0.5 rounded border-white/10 bg-white/5 text-white focus:ring-white/20"
                            />
                            <span className="text-slate-400">
                                I agree to the{" "}
                                <Link href="#" className="text-white hover:underline">
                                    Terms of Service
                                </Link>{" "}
                                and{" "}
                                <Link href="#" className="text-white hover:underline">
                                    Privacy Policy
                                </Link>
                            </span>
                        </label>

                        <Button
                            type="submit"
                            disabled={loading}
                            className="w-full h-14 bg-white text-black font-black text-sm hover:bg-white/90 disabled:opacity-50 rounded-xl group"
                        >
                            {loading ? "CREATING ACCOUNT..." : (
                                <>
                                    CREATE ACCOUNT <ArrowRight className="ml-2 w-4 h-4 group-hover:translate-x-1 transition-transform" />
                                </>
                            )}
                        </Button>
                    </form>

                    <div className="mt-8 text-center text-sm">
                        <span className="text-slate-500">Already have an account? </span>
                        <Link href="/projects/e-commerce/auth/login" className="text-white font-bold hover:underline">
                            Sign in
                        </Link>
                    </div>
                </motion.div>

                <div className="mt-8 text-center">
                    <Link href="/projects/e-commerce" className="text-slate-500 hover:text-white text-sm transition-colors">
                         Back to Store
                    </Link>
                </div>
            </div>
        </div>
    );
}

"use client";

import { useMemo, useEffect, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import {
    ShoppingCart,
    Trash2,
    ArrowLeft,
    ArrowRight,
    Minus,
    Plus,
    CreditCard,
    ShieldCheck,
    Zap
} from "lucide-react";
import Link from "next/link";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { PRODUCTS, useCartStore } from "../store";

export default function CartPage() {
    const [mounted, setMounted] = useState(false);
    const cart = useCartStore((state) => state.cart);
    const removeFromCart = useCartStore((state) => state.removeFromCart);
    const updateQuantity = useCartStore((state) => state.updateQuantity);
    const clearCart = useCartStore((state) => state.clearCart);

    useEffect(() => {
        const timer = setTimeout(() => {
            setMounted(true);
        }, 0);
        return () => clearTimeout(timer);
    }, []);

    const cartTotal = useMemo(() => {
        return cart.reduce((acc, item) => {
            const product = PRODUCTS.find(p => p.id === item.productId);
            return acc + (product?.price || 0) * item.quantity;
        }, 0);
    }, [cart]);

    const shipping = cartTotal > 500 ? 0 : 25;
    const tax = cartTotal * 0.08;
    const grandTotal = cartTotal + shipping + tax;

    if (!mounted) return null;

    return (
        <div className="min-h-screen bg-[#050510] text-[#E0E0E0] font-sans selection:bg-blue-500/30">
            <header className="fixed top-0 left-0 right-0 z-50 bg-[#050510]/80 backdrop-blur-xl border-b border-white/5">
                <div className="container mx-auto px-6 h-20 flex items-center justify-between">
                    <Link href="/projects/e-commerce" className="text-2xl font-black tracking-tighter text-white hover:opacity-80 transition-opacity">
                        ZENITH.
                    </Link>
                    <div className="text-xs font-black uppercase tracking-widest text-[#606060]">
                        Secure Portal
                    </div>
                </div>
            </header>

            <main className="pt-32 pb-24">
                <div className="container mx-auto px-6 max-w-6xl">
                    <div className="flex flex-col lg:flex-row gap-16 items-start">

                        {/* Cart List */}
                        <div className="flex-1 w-full">
                            <div className="flex items-center justify-between mb-8 pb-6 border-b border-white/5">
                                <h1 className="text-4xl font-black text-white tracking-tighter">SHOPPING BAG.</h1>
                                <span className="text-[#606060] font-bold">{cart.length} Items</span>
                            </div>

                            {cart.length > 0 ? (
                                <div className="space-y-8">
                                    <AnimatePresence mode="popLayout">
                                        {cart.map((item) => {
                                            const product = PRODUCTS.find(p => p.id === item.productId);
                                            if (!product) return null;
                                            return (
                                                <motion.div
                                                    layout
                                                    key={item.productId}
                                                    initial={{ opacity: 0, y: 10 }}
                                                    animate={{ opacity: 1, y: 0 }}
                                                    exit={{ opacity: 0, x: -20 }}
                                                    className="flex flex-col sm:flex-row gap-8 p-6 bg-white/2 border border-white/10 rounded-[2rem] group"
                                                >
                                                    <div className="w-32 h-32 rounded-2xl overflow-hidden bg-[#0A0A15] border border-white/10 shrink-0">
                                                        <img src={product.image} className="w-full h-full object-cover" />
                                                    </div>

                                                    <div className="flex-1 flex flex-col justify-between py-1">
                                                        <div>
                                                            <div className="flex items-start justify-between gap-4">
                                                                <Link href={`/projects/e-commerce/product/${product.id}`}>
                                                                    <h3 className="text-xl font-bold text-white hover:text-blue-500 transition-colors uppercase">{product.name}</h3>
                                                                </Link>
                                                                <button
                                                                    onClick={() => removeFromCart(product.id)}
                                                                    className="p-2 text-[#404040] hover:text-red-500 transition-colors"
                                                                >
                                                                    <Trash2 className="w-5 h-5" />
                                                                </button>
                                                            </div>
                                                            <p className="text-xs font-bold text-blue-500/60 uppercase tracking-widest mt-1">{product.category}</p>
                                                        </div>

                                                        <div className="flex items-center justify-between mt-6">
                                                            <div className="flex items-center bg-white/5 border border-white/10 rounded-xl p-1">
                                                                <button
                                                                    onClick={() => updateQuantity(product.id, Math.max(1, item.quantity - 1))}
                                                                    className="w-8 h-8 flex items-center justify-center text-[#606060] hover:text-white"
                                                                >
                                                                    <Minus className="w-3 h-3" />
                                                                </button>
                                                                <span className="w-8 text-center text-sm font-bold">{item.quantity}</span>
                                                                <button
                                                                    onClick={() => updateQuantity(product.id, item.quantity + 1)}
                                                                    className="w-8 h-8 flex items-center justify-center text-[#606060] hover:text-white"
                                                                >
                                                                    <Plus className="w-3 h-3" />
                                                                </button>
                                                            </div>
                                                            <div className="text-xl font-black text-white">
                                                                ${(product.price * item.quantity).toFixed(2)}
                                                            </div>
                                                        </div>
                                                    </div>
                                                </motion.div>
                                            );
                                        })}
                                    </AnimatePresence>

                                    <button
                                        onClick={clearCart}
                                        className="text-xs font-black uppercase tracking-widest text-[#404040] hover:text-white transition-colors flex items-center gap-2"
                                    >
                                        Clear Cart
                                    </button>
                                </div>
                            ) : (
                                <div className="py-24 flex flex-col items-center justify-center text-center">
                                    <div className="w-20 h-20 rounded-full bg-white/5 flex items-center justify-center mb-6">
                                        <ShoppingCart className="w-10 h-10 text-[#404040]" />
                                    </div>
                                    <h2 className="text-2xl font-bold text-white mb-2">Cart is empty</h2>
                                    <p className="text-[#606060] mb-8">Start adding tech to your setup today.</p>
                                    <Link href="/projects/e-commerce">
                                        <Button variant="outline" className="h-14 px-8 rounded-full border-white/10 bg-transparent text-white font-bold hover:bg-white/5">
                                            Return to Shop
                                        </Button>
                                    </Link>
                                </div>
                            )}
                        </div>

                        {/* Order Summary */}
                        <div className="w-full lg:w-[400px]">
                            <div className="sticky top-32 p-10 bg-white/2 border border-blue-500/10 rounded-[3rem] shadow-2xl shadow-blue-500/5">
                                <h2 className="text-2xl font-black text-white tracking-tighter mb-10">SUMMARY.</h2>

                                <div className="space-y-6 mb-10">
                                    <div className="flex justify-between text-sm">
                                        <span className="text-[#A0A0A0] font-medium">Subtotal</span>
                                        <span className="text-white font-bold">${cartTotal.toFixed(2)}</span>
                                    </div>
                                    <div className="flex justify-between text-sm">
                                        <span className="text-[#A0A0A0] font-medium">Shipping</span>
                                        <span className="text-white font-bold">{shipping === 0 ? "FREE" : `$${shipping.toFixed(2)}`}</span>
                                    </div>
                                    <div className="flex justify-between text-sm">
                                        <span className="text-[#A0A0A0] font-medium">Tax</span>
                                        <span className="text-white font-bold">${tax.toFixed(2)}</span>
                                    </div>
                                    <div className="pt-6 border-t border-white/10 flex justify-between">
                                        <span className="text-xl font-black text-white">TOTAL.</span>
                                        <span className="text-xl font-black text-blue-500">${grandTotal.toFixed(2)}</span>
                                    </div>
                                </div>

                                <div className="space-y-4">
                                    <Link href="/projects/e-commerce/success">
                                        <Button
                                            disabled={cart.length === 0}
                                            className="w-full h-16 rounded-[2rem] bg-blue-600 hover:bg-blue-700 text-white font-black text-lg tracking-tight shadow-xl shadow-blue-600/20 active:scale-95 transition-all"
                                        >
                                            CHECKOUT NOW <ArrowRight className="ml-2 w-5 h-5" />
                                        </Button>
                                    </Link>
                                    <p className="text-[10px] text-[#404040] text-center font-bold uppercase tracking-widest flex items-center justify-center gap-2">
                                        <ShieldCheck className="w-3 h-3 text-emerald-500" />
                                        Encrypted & Secure Transaction
                                    </p>
                                </div>

                                <div className="mt-12 p-6 bg-blue-500/5 rounded-2xl border border-blue-500/10">
                                    <div className="flex items-center gap-3 mb-2">
                                        <Zap className="w-4 h-4 text-blue-500" />
                                        <h4 className="text-xs font-black text-white uppercase tracking-widest">Zenith Perks</h4>
                                    </div>
                                    <p className="text-[11px] text-[#A0A0A0] leading-relaxed">
                                        Orders over $1,000 include complimentary Priority Express shipping and setup assistance.
                                    </p>
                                </div>
                            </div>
                        </div>

                    </div>
                </div>
            </main>

            <footer className="container mx-auto px-6 py-12 border-t border-white/5 text-center mt-24">
                <Link href="/projects/e-commerce" className="inline-flex items-center gap-2 text-primary font-bold hover:underline mb-4">
                    <ArrowLeft className="w-4 h-4" /> Continue Shopping
                </Link>
                <p className="text-[#404040] text-sm">Zenith Technology Inc.  2026</p>
            </footer>
        </div>
    );
}

"use client";

import { useState } from "react";
import { motion } from "framer-motion";
import {
    ShoppingCart,
    Star,
    Heart,
    Eye,
    ArrowLeft,
} from "lucide-react";
import Link from "next/link";
import { useSearchParams } from "next/navigation";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { PRODUCTS, useCartStore, type Product } from "../store";

const CATEGORIES = ["All", "Audio", "Wearables", "Computing", "Accessories"];

export default function CategoryPage() {
    const searchParams = useSearchParams();
    const category = searchParams.get("category") || "All";

    const addToCart = useCartStore((state: any) => state.addToCart);
    const wishlist = useCartStore((state: any) => state.wishlist);
    const toggleWishlist = useCartStore((state: any) => state.toggleWishlist);

    const filteredProducts = PRODUCTS.filter((p) =>
        category === "All" ? true : p.category === category
    );

    const categoryInfo = {
        Audio: {
            title: "Premium Audio",
            description: "Experience sound like never before with our cutting-edge audio technology.",
        },
        Wearables: {
            title: "Smart Wearables",
            description: "Stay connected with intelligent devices that fit your lifestyle.",
        },
        Computing: {
            title: "Next-Gen Computing",
            description: "Power through your day with revolutionary computing solutions.",
        },
        Accessories: {
            title: "Essential Accessories",
            description: "Complete your setup with premium accessories designed for perfection.",
        },
    };

    const info = categoryInfo[category as keyof typeof categoryInfo];

    return (
        <div className="min-h-screen bg-[#020205] text-[#E0E0E0]">
            {/* Header */}
            <header className="fixed top-0 left-0 right-0 z-50 bg-[#020205]/80 backdrop-blur-xl border-b border-white/5">
                <div className="container mx-auto px-6 h-20 flex items-center justify-between">
                    <Link href="/projects/e-commerce" className="text-2xl font-black tracking-tighter text-white">
                        ZENITH.
                    </Link>
                    <nav className="hidden md:flex items-center gap-8">
                        {CATEGORIES.map((cat) => (
                            <Link
                                key={cat}
                                href={`/projects/e-commerce/category?category=${cat}`}
                                className={cn(
                                    "text-sm font-semibold transition-colors",
                                    category === cat ? "text-white" : "text-slate-500 hover:text-white"
                                )}
                            >
                                {cat}
                            </Link>
                        ))}
                    </nav>
                </div>
            </header>

            <div className="container mx-auto px-6 pt-32 pb-24">
                {/* Back Button */}
                <Link
                    href="/projects/e-commerce"
                    className="inline-flex items-center gap-2 text-slate-500 hover:text-white mb-8 transition-colors"
                >
                    <ArrowLeft className="w-4 h-4" />
                    Back to Shop
                </Link>

                {/* Category Hero */}
                {info && (
                    <div className="mb-16 text-center max-w-2xl mx-auto">
                        <motion.div
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                        >
                            <h1 className="text-5xl md:text-6xl font-black mb-6 tracking-tight">
                                {info.title}
                            </h1>
                            <p className="text-lg text-slate-400 leading-relaxed">
                                {info.description}
                            </p>
                        </motion.div>
                    </div>
                )}

                {/* Products Count */}
                <div className="flex items-center justify-between mb-12 pb-6 border-b border-white/5">
                    <p className="text-slate-500">
                        {filteredProducts.length} {filteredProducts.length === 1 ? "product" : "products"}
                    </p>
                </div>

                {/* Products Grid */}
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-10">
                    {filteredProducts.map((p, idx) => (
                        <motion.div
                            key={p.id}
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            transition={{ delay: idx * 0.05 }}
                            className="group relative flex flex-col bg-white/2 border border-white/5 hover:border-white/20 rounded-[2.5rem] p-5 transition-all duration-500"
                        >
                            {/* Badges */}
                            {p.newArrival && (
                                <div className="absolute top-8 left-8 z-10 px-3 py-1 bg-emerald-600 text-[10px] font-black uppercase tracking-widest rounded-full">
                                    New
                                </div>
                            )}
                            {p.trending && !p.newArrival && (
                                <div className="absolute top-8 left-8 z-10 px-3 py-1 bg-blue-600 text-[10px] font-black uppercase tracking-widest rounded-full">
                                    Trending
                                </div>
                            )}
                            {p.salePrice && (
                                <div className="absolute top-8 right-20 z-10 px-3 py-1 bg-red-600 text-[10px] font-black uppercase tracking-widest rounded-full">
                                    Sale
                                </div>
                            )}

                            {/* Quick Actions */}
                            <div className="absolute top-8 right-8 z-10 flex flex-col gap-2 translate-x-4 opacity-0 group-hover:translate-x-0 group-hover:opacity-100 transition-all duration-300">
                                <button
                                    onClick={() => toggleWishlist(p.id)}
                                    className={cn(
                                        "w-10 h-10 rounded-full backdrop-blur-md border flex items-center justify-center transition-all",
                                        wishlist.includes(p.id)
                                            ? "bg-red-500 border-red-500 text-white"
                                            : "bg-white/10 border-white/20 text-white hover:bg-white hover:text-black"
                                    )}
                                >
                                    <Heart className={cn("w-4 h-4", wishlist.includes(p.id) && "fill-current")} />
                                </button>
                                <Link
                                    href={`/projects/e-commerce/product/${p.id}`}
                                    className="w-10 h-10 rounded-full bg-white/10 backdrop-blur-md border border-white/20 flex items-center justify-center text-white hover:bg-white hover:text-black"
                                >
                                    <Eye className="w-4 h-4" />
                                </Link>
                            </div>

                            {/* Product Image */}
                            <Link href={`/projects/e-commerce/product/${p.id}`} className="relative aspect-[4/5] rounded-[2rem] overflow-hidden mb-8 bg-[#0A0A15] block">
                                <img
                                    src={p.image}
                                    alt={p.name}
                                    className="w-full h-full object-cover transition-transform duration-700 group-hover:scale-110"
                                />
                            </Link>

                            {/* Product Info */}
                            <div className="px-3 flex-1">
                                <div className="flex items-center justify-between mb-3">
                                    <span className="text-xs font-bold text-blue-500 uppercase tracking-widest">{p.category}</span>
                                    <div className="flex items-center gap-1 text-[#606060] font-bold text-xs">
                                        <Star className="w-3 h-3 text-amber-500 fill-amber-500" />
                                        {p.rating}
                                    </div>
                                </div>
                                <Link href={`/projects/e-commerce/product/${p.id}`} className="block">
                                    <h3 className="text-2xl font-bold text-white mb-2 leading-tight hover:text-blue-500 transition-colors">
                                        {p.name}
                                    </h3>
                                </Link>
                                <p className="text-[#606060] text-sm mb-6 leading-relaxed line-clamp-2">{p.description}</p>

                                <div className="mt-auto pt-6 flex items-center justify-between border-t border-white/5">
                                    <div>
                                        {p.salePrice ? (
                                            <div className="flex items-baseline gap-2">
                                                <span className="text-2xl font-black text-white">${p.salePrice}</span>
                                                <span className="text-base font-bold text-[#606060] line-through">${p.price}</span>
                                            </div>
                                        ) : (
                                            <span className="text-2xl font-black text-white">${p.price}</span>
                                        )}
                                        {p.stock < 10 && p.stock > 0 && (
                                            <p className="text-[10px] font-bold text-amber-500 mt-1">Only {p.stock} left</p>
                                        )}
                                    </div>
                                    <Button
                                        onClick={() => addToCart(p.id)}
                                        disabled={p.stock === 0}
                                        className="h-12 w-12 rounded-2xl bg-white text-black hover:bg-blue-600 hover:text-white p-0 transition-colors disabled:opacity-50"
                                    >
                                        <ShoppingCart className="w-5 h-5" />
                                    </Button>
                                </div>
                            </div>
                        </motion.div>
                    ))}
                </div>
            </div>
        </div>
    );
}

"use client";

import { useState, useEffect, useMemo } from "react";
import Link from "next/link";
import { useRouter } from "next/navigation";
import { motion, AnimatePresence } from "framer-motion";
import {
    ArrowLeft,
    CreditCard,
    MapPin,
    Package,
    Lock,
    Check,
    Truck,
    Zap,
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { supabase } from "@/lib/supabase";
import { useCartStore, PRODUCTS, CartItem } from "../store";
import { cn } from "@/lib/utils";
import { User as AuthUser } from "@supabase/supabase-js";

export default function CheckoutPage() {
    const router = useRouter();
    const { cart, clearCart } = useCartStore((state) => ({
        cart: state.cart,
        clearCart: state.clearCart
    }));
    const [user, setUser] = useState<AuthUser | null>(null);
    const [step, setStep] = useState<"shipping" | "payment" | "review">("shipping");
    const [processing, setProcessing] = useState(false);

    const [shippingData, setShippingData] = useState({
        fullName: "",
        email: "",
        phone: "",
        address: "",
        city: "",
        state: "",
        zip: "",
        country: "USA",
    });

    const [paymentData, setPaymentData] = useState({
        cardNumber: "",
        cardName: "",
        expiry: "",
        cvv: "",
    });

    const [shippingMethod, setShippingMethod] = useState("standard");

    useEffect(() => {
        const checkUser = async () => {
            const { data: { user } } = await supabase.auth.getUser();
            if (user) {
                setUser(user);
                setShippingData((prev: typeof shippingData) => ({
                    ...prev,
                    fullName: user.user_metadata?.full_name || "",
                    email: user.email || "",
                }));
            }
        };
        checkUser();
    }, []);

    const cartItems = useMemo(() => {
        return cart.map((item) => {
            const product = PRODUCTS.find((p) => p.id === item.productId);
            return { ...item, product };
        });
    }, [cart]);

    const subtotal = useMemo(() => {
        return cartItems.reduce((sum: number, item) => {
            const price = item.product?.salePrice || item.product?.price || 0;
            return sum + price * item.quantity;
        }, 0);
    }, [cartItems]);

    const shippingCost = shippingMethod === "express" ? 19.99 : shippingMethod === "overnight" ? 39.99 : 5.99;
    const tax = subtotal * 0.08;
    const total = subtotal + shippingCost + tax;

    const handlePlaceOrder = async () => {
        setProcessing(true);
        // Simulate order processing
        setTimeout(() => {
            clearCart();
            router.push("/projects/e-commerce/success");
        }, 2000);
    };

    if (cart.length === 0 && !processing) {
        return (
            <div className="min-h-screen bg-[#020205] text-[#E0E0E0] flex items-center justify-center">
                <div className="text-center">
                    <Package className="w-16 h-16 mx-auto mb-6 text-slate-700" />
                    <h2 className="text-2xl font-black mb-2">Your cart is empty</h2>
                    <p className="text-slate-500 mb-8">Add some items to checkout</p>
                    <Button
                        onClick={() => router.push("/projects/e-commerce")}
                        className="h-12 px-8 rounded-xl bg-white text-black font-black"
                    >
                        Continue Shopping
                    </Button>
                </div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-[#020205] text-[#E0E0E0]">
            <div className="container mx-auto px-6 py-12">
                {/* Back Button */}
                <Link
                    href="/projects/e-commerce"
                    className="inline-flex items-center gap-2 text-slate-500 hover:text-white mb-8 transition-colors"
                >
                    <ArrowLeft className="w-4 h-4" />
