                                                {processing ? "Processing..." : `Place Order  $${total.toFixed(2)}`}
                                            </Button>
                                        </div>
                                    </div>
                                </motion.div>
                            )}
                        </AnimatePresence>
                    </div>

                    {/* Order Summary Sidebar */}
                    <div className="lg:col-span-1">
                        <div className="sticky top-24 p-8 rounded-3xl bg-white/5 border border-white/10">
                            <h3 className="text-xl font-black mb-6">Order Summary</h3>

                            <div className="space-y-4 mb-6">
                                {cartItems.map((item) => (
                                    <div key={item.productId} className="flex gap-4">
                                        <img
                                            src={item.product?.image}
                                            alt={item.product?.name}
                                            className="w-16 h-16 rounded-lg object-cover"
                                        />
                                        <div className="flex-1">
                                            <p className="font-bold text-sm">{item.product?.name}</p>
                                            <p className="text-xs text-slate-500">Qty: {item.quantity}</p>
                                        </div>
                                        <span className="font-bold text-sm">
                                            ${((item.product?.salePrice || item.product?.price || 0) * item.quantity).toFixed(2)}
                                        </span>
                                    </div>
                                ))}
                            </div>

                            <div className="space-y-3 py-6 border-t border-white/5 text-sm">
                                <div className="flex justify-between">
                                    <span className="text-slate-500">Subtotal</span>
                                    <span className="font-bold">${subtotal.toFixed(2)}</span>
                                </div>
                                <div className="flex justify-between">
                                    <span className="text-slate-500">Shipping</span>
                                    <span className="font-bold">${shippingCost.toFixed(2)}</span>
                                </div>
                                <div className="flex justify-between">
                                    <span className="text-slate-500">Tax</span>
                                    <span className="font-bold">${tax.toFixed(2)}</span>
                                </div>
                            </div>

                            <div className="flex justify-between pt-6 border-t border-white/5">
                                <span className="text-lg font-black">Total</span>
                                <span className="text-2xl font-black">${total.toFixed(2)}</span>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

"use client";

import { createContext, useContext, useEffect, useState, ReactNode } from 'react';
import { User, Session, AuthError } from '@supabase/supabase-js';
import { supabase } from '@/lib/supabase';

interface AuthContextType {
    user: User | null;
    session: Session | null;
    loading: boolean;
    signIn: (email: string, password: string) => Promise<{ error: AuthError | null }>;
    signUp: (email: string, password: string, metadata?: Record<string, any>) => Promise<{ error: AuthError | null }>;
    signOut: () => Promise<void>;
    updateProfile: (data: Record<string, any>) => Promise<{ error: AuthError | null }>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function AuthProvider({ children }: { children: React.ReactNode }) {
    const [user, setUser] = useState<User | null>(null);
    const [session, setSession] = useState<Session | null>(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Get initial session
        supabase.auth.getSession().then(({ data: { session } }) => {
            setSession(session);
            setUser(session?.user ?? null);
            setLoading(false);
        });

        // Listen for auth changes
        const {
            data: { subscription },
        } = supabase.auth.onAuthStateChange((_event, session) => {
            setSession(session);
            setUser(session?.user ?? null);
        });

        return () => subscription.unsubscribe();
    }, []);

    const signIn = async (email: string, password: string) => {
        const { error } = await supabase.auth.signInWithPassword({
            email,
            password,
        });
        return { error };
    };

    const signUp = async (email: string, password: string, metadata?: any) => {
        const { error } = await supabase.auth.signUp({
            email,
            password,
            options: {
                data: metadata,
            },
        });
        return { error };
    };

    const signOut = async () => {
        await supabase.auth.signOut();
    };

    const updateProfile = async (data: Record<string, any>) => {
        if (!user) {
            return {
                error: {
                    message: 'No user',
                    name: 'AuthError',
                    status: 401
                } as AuthError
            };
        }

        const { error } = await supabase.auth.updateUser({
            data,
        });
        return { error };
    };

    return (
        <AuthContext.Provider
            value={{
                user,
                session,
                loading,
                signIn,
                signUp,
                signOut,
                updateProfile,
            }}
        >
            {children}
        </AuthContext.Provider>
    );
}

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (context === undefined) {
        throw new Error('useAuth must be used within an AuthProvider');
    }
    return context;
};

#!/usr/bin/env node

/**
 * Database Migration Script for E-Commerce Supabase Setup
 * 
 * This script automatically creates all necessary tables in your Supabase database.
 * 
 * Usage:
 *   node setup-database.js
 * 
 * Requirements:
 *   - @supabase/supabase-js installed
 *   - .env.local configured with NEXT_PUBLIC_SUPABASE_URL and SUPABASE_SERVICE_ROLE_KEY
 */

import { createClient } from '@supabase/supabase-js';
import fs from 'fs';
import path from 'path';
import { fileURLToPath } from 'url';
import dotenv from 'dotenv';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// Load environment variables
dotenv.config({ path: path.join(__dirname, '../../../.env.local') });

// ANSI color codes for pretty output
const colors = {
    reset: '\x1b[0m',
    bright: '\x1b[1m',
    green: '\x1b[32m',
    red: '\x1b[31m',
    yellow: '\x1b[33m',
    blue: '\x1b[34m',
    cyan: '\x1b[36m',
};

function log(message, color = 'reset') {
    console.log(`${colors[color]}${message}${colors.reset}`);
}

function logSection(title) {
    console.log('\n' + colors.cyan + ''.repeat(60) + colors.reset);
    console.log(colors.bright + colors.cyan + `  ${title}` + colors.reset);
    console.log(colors.cyan + ''.repeat(60) + colors.reset + '\n');
}

async function setupDatabase() {
    const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL;
    const supabaseServiceKey = process.env.SUPABASE_SERVICE_ROLE_KEY;

    logSection(' E-Commerce Database Setup');

    if (!supabaseUrl || !supabaseServiceKey) {
        log(' Error: Missing Supabase credentials', 'red');
        log('Please ensure these variables are set in .env.local:', 'yellow');
        log('  - NEXT_PUBLIC_SUPABASE_URL', 'blue');
        log('  - SUPABASE_SERVICE_ROLE_KEY', 'blue');
        process.exit(1);
    }

    log(` Supabase URL: ${supabaseUrl}`, 'blue');

    // Create client
    const supabase = createClient(supabaseUrl, supabaseServiceKey);

    // Test connection
    logSection(' Testing Database Connection');
    const { data, error } = await supabase.from('products').select('count', { count: 'exact', head: true });

    if (!error) {
        log(' Successfully connected to Supabase!', 'green');
        log(` Products table already exists`, 'green');
        logSection(' Database Already Set Up!');
        log('Your database appears to be ready.', 'green');
        return;
    }

    // Tables don't exist yet - provide setup instructions
    logSection(' Database Setup Instructions');
    log('The database tables need to be created. Follow these steps:', 'yellow');
    log('', 'reset');

    log('STEP 1: Open Supabase SQL Editor', 'bright');
    const dashboardUrl = supabaseUrl.replace('supabase.co', 'supabase.com').replace('//', '//app.');
    log(`   ${dashboardUrl}/editor/sql`, 'cyan');
    log('', 'reset');

    log('STEP 2: Copy Schema File', 'bright');
    const schemaPath = path.join(__dirname, 'schema.sql');
    log(`   File: ${schemaPath}`, 'cyan');

    // Try to display first few lines of schema
    try {
        const schema = fs.readFileSync(schemaPath, 'utf8');
        log(`   File size: ${(schema.length / 1024).toFixed(1)} KB`, 'cyan');
        log('', 'reset');

        log('STEP 3: Execute Schema', 'bright');
        log('  1. Copy the entire contents of schema.sql', 'blue');
        log('  2. Paste it into the SQL Editor', 'blue');
        log('  3. Click "Run" to create all tables', 'blue');
        log('', 'reset');

        log('STEP 4: (Optional) Add Sample Data', 'bright');
        const seedPath = path.join(__dirname, 'seed.sql');
        log(`   File: ${seedPath}`, 'cyan');
        log('   Execute seed.sql in SQL Editor to add sample products', 'blue');
        log('', 'reset');

        logSection(' What Gets Created');
        log('Tables:', 'bright');
        log('   products - Product catalog', 'green');
        log('   customers - Customer profiles', 'green');
        log('   orders - Order management', 'green');
        log('   order_items - Line items', 'green');
        log('   reviews - Product reviews', 'green');
        log('   wishlist - User wishlists', 'green');
        log('   cart_items - Shopping carts', 'green');
        log('   categories - Product categories', 'green');
        log('', 'reset');
        log('Plus: Indexes, RLS policies, and triggers', 'yellow');

    } catch (err) {
        log(` Could not read schema file: ${err.message}`, 'yellow');
    }

    logSection(' Quick Start Alternative');
    log('If you have Supabase CLI installed:', 'bright');
    log('  1. npm install -g supabase', 'blue');
    log('  2. supabase login', 'blue');
    log('  3. supabase db push', 'blue');
    log('', 'reset');

    logSection(' Next Steps');
    log('After creating the tables:', 'bright');
    log('   Your E-Commerce app will automatically connect', 'cyan');
    log('   All features (cart, wishlist, orders) will work', 'cyan');
    log('   Admin panel will have live data', 'cyan');
    log('', 'reset');
}

// Run the setup
setupDatabase().catch((err) => {
    log(`\n Error: ${err.message}`, 'red');
    process.exit(1);
});

"use client";

import { useState, useMemo, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import {
    ShoppingCart,
    Search,
    Menu,
    X,
    Star,
    ArrowRight,
    ChevronDown,
    Filter,
    CreditCard,
    Truck,
    ShieldCheck,
    RefreshCw,
    Heart,
    Eye,
    ArrowLeft,
    User
} from "lucide-react";
import Link from "next/link";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { PRODUCTS, useCartStore } from "./store";

const CATEGORIES = ["All", "Audio", "Wearables", "Computing", "Accessories"];

export default function ECommerceStorefront() {
    const [activeCategory, setActiveCategory] = useState("All");
    const [searchQuery, setSearchQuery] = useState("");
    const [isCartOpen, setIsCartOpen] = useState(false);
    const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
    const [mounted, setMounted] = useState(false);

    const cart = useCartStore((state) => state.cart);
    const addToCart = useCartStore((state) => state.addToCart);
    const removeFromCart = useCartStore((state) => state.removeFromCart);
    const wishlist = useCartStore((state) => state.wishlist);
    const toggleWishlist = useCartStore((state) => state.toggleWishlist);


    // Fix hydration mismatch for zustand persist
    useEffect(() => {
        const timer = setTimeout(() => {
            setMounted(true);
        }, 0);
        return () => clearTimeout(timer);
    }, []);

    const filteredProducts = useMemo(() => {
        return PRODUCTS.filter(p => {
            const matchesCategory = activeCategory === "All" || p.category === activeCategory;
            const matchesSearch = p.name.toLowerCase().includes(searchQuery.toLowerCase());
            return matchesCategory && matchesSearch;
        });
    }, [activeCategory, searchQuery]);

    const cartItemsCount = useMemo(() => {
        return cart.reduce((acc, item) => acc + item.quantity, 0);
    }, [cart]);

    const cartTotal = useMemo(() => {
        return cart.reduce((acc, item) => {
            const product = PRODUCTS.find(p => p.id === item.productId);
            const price = product?.salePrice || product?.price || 0;
            return acc + price * item.quantity;
        }, 0);
    }, [cart]);

    if (!mounted) return null;

    return (
        <div className="min-h-screen bg-[#050510] text-[#E0E0E0] font-sans selection:bg-blue-500/30">
            {/* --- Sticky Header --- */}
            <header className="fixed top-0 left-0 right-0 z-50 bg-[#050510]/80 backdrop-blur-xl border-b border-white/5">
                <div className="container mx-auto px-6 h-20 flex items-center justify-between">
                    <div className="flex items-center gap-12">
                        <Link href="/projects/e-commerce" className="text-2xl font-black tracking-tighter text-white hover:opacity-80 transition-opacity">
                            ZENITH.
                        </Link>
                        <nav className="hidden lg:flex items-center gap-8 text-sm font-medium text-[#A0A0A0]">
                            {CATEGORIES.map(cat => (
                                <button
                                    key={cat}
                                    onClick={() => setActiveCategory(cat)}
                                    className={cn(
                                        "hover:text-white transition-colors relative py-1",
                                        activeCategory === cat && "text-white"
                                    )}
                                >
                                    {cat}
                                    {activeCategory === cat && (
                                        <motion.div layoutId="navunderline" className="absolute -bottom-1 left-0 right-0 h-0.5 bg-blue-500" />
                                    )}
                                </button>
                            ))}
                        </nav>
                    </div>

                    <div className="flex items-center gap-5">
                        <div className="hidden md:flex items-center bg-white/5 border border-white/10 rounded-full px-4 h-10 w-64 focus-within:border-blue-500/50 transition-colors">
                            <Search className="w-4 h-4 text-[#606060]" />
                            <input
                                type="text"
                                placeholder="Search products..."
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                                className="bg-transparent border-none outline-none text-sm px-3 w-full placeholder:text-[#606060]"
                            />
                        </div>

                        <button
                            onClick={() => setIsCartOpen(true)}
                            className="relative p-2 text-[#A0A0A0] hover:text-white transition-colors"
                        >
                            <ShoppingCart className="w-6 h-6" />
                            {cartItemsCount > 0 && (
                                <span className="absolute top-0 right-0 w-4 h-4 bg-blue-600 text-[10px] font-bold text-white flex items-center justify-center rounded-full">
                                    {cartItemsCount}
                                </span>
                            )}
                        </button>

                        <Link
                            href="/projects/e-commerce/account"
                            className="hidden md:block p-2 text-[#A0A0A0] hover:text-white transition-colors"
                        >
                            <User className="w-6 h-6" />
                        </Link>

                        <button
                            className="lg:hidden p-2"
                            onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                        >
                            {isMobileMenuOpen ? <X /> : <Menu />}
                        </button>
                    </div>
                </div>
            </header>

            {/* --- Main Content --- */}
            <main className="pt-32 pb-24">
                {/* Banner Section */}
                <section className="container mx-auto px-6 mb-20">
                    <div className="relative rounded-[3rem] overflow-hidden bg-gradient-to-br from-[#0A0A20] to-[#050510] border border-white/5 p-12 lg:p-24 flex flex-col items-center text-center">
                        <div className="absolute inset-0 pointer-events-none">
                            <div className="absolute top-0 left-1/4 w-[500px] h-[500px] bg-blue-600/10 blur-[150px] rounded-full" />
                            <div className="absolute bottom-0 right-1/4 w-[500px] h-[500px] bg-purple-600/10 blur-[150px] rounded-full" />
                        </div>

                        <motion.div
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            className="relative z-10 max-w-3xl"
                        >
                            <h1 className="text-5xl md:text-8xl font-black tracking-tight text-white mb-8 leading-[0.9]">
                                FUTURE OF <br /> <span className="text-blue-500">HARDWARE.</span>
                            </h1>
                            <p className="text-[#A0A0A0] text-lg md:text-xl mb-12 max-w-2xl mx-auto leading-relaxed">
                                Experience high-fidelity technology designed for the modern desktop. Precision engineering meets uncompromising aesthetic.
                            </p>
                            <div className="flex flex-wrap items-center justify-center gap-4">
                                <Button size="lg" className="h-14 px-10 rounded-full bg-white text-black hover:bg-white/90 font-bold transition-transform active:scale-95">
                                    Shop Zenith Pro
                                </Button>
                                <Link href="/projects" className="inline-flex items-center justify-center h-14 px-10 rounded-full border border-white/20 bg-transparent text-white font-bold hover:bg-white/5">
                                    View All Projects
